import * as React from 'react';
import Paper from '@mui/material/Paper';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import axios from 'axios';
import { API_PATHS } from '../../../../../../configs/api';
import Cookies from 'js-cookie';
import { ACCESS_TOKEN_KEY } from '../../../../../../utils/constants';
import TableRows from './TableRows';

interface Column {
  id:
    | 'staff_id'
    | 'name'
    | 'gender'
    | 'card_number'
    | 'bank_account_no'
    | 'family_card_number'
    | 'marriage_code'
    | 'mother_name'
    | 'pob'
    | 'dob'
    | 'home_address_1'
    | 'home_address_2'
    | 'nc_id'
    | 'contract_start_date'
    | 'first_contract_date'
    | 'secound_contract_date'
    | 'deleted_at'
    | 'department_name'
    | 'type'
    | 'basic_salary'
    | 'position_name'
    | 'operational_allowance_paid'
    | 'meal_allowance_paid'
    | 'meal_allowance'
    | 'grade_name';
  label: string;
  minWidth?: number;
}

const columns: readonly Column[] = [
  { id: 'staff_id', label: 'NIK', minWidth: 95 },
  { id: 'name', label: 'Name', minWidth: 150 },
  { id: 'gender', label: 'Gender', minWidth: 70 },
  { id: 'card_number', label: 'Bank Card No.', minWidth: 130 },
  { id: 'bank_account_no', label: 'Bank Account No.', minWidth: 150 },
  { id: 'family_card_number', label: 'Family Card No.', minWidth: 150 },
  { id: 'marriage_code', label: 'Marriage Status', minWidth: 130 },
  { id: 'mother_name', label: 'Mother Name', minWidth: 150 },
  { id: 'pob', label: 'Place of birth', minWidth: 115 },
  { id: 'dob', label: 'Date of birth', minWidth: 115 },
  { id: 'home_address_1', label: 'Home Address', minWidth: 350 },
  { id: 'home_address_2', label: 'Home Address', minWidth: 350 },
  { id: 'nc_id', label: 'National Card ID No.', minWidth: 170 },
  { id: 'contract_start_date', label: 'Date Start', minWidth: 90 },
  { id: 'first_contract_date', label: 'First Contract', minWidth: 110 },
  { id: 'secound_contract_date', label: 'Secound Contract', minWidth: 130 },
  { id: 'deleted_at', label: 'End Contract', minWidth: 110 },
  { id: 'department_name', label: 'Department', minWidth: 150 },
  { id: 'type', label: 'Employee Type', minWidth: 120 },
  { id: 'basic_salary', label: 'Salary Rp.', minWidth: 90 },
  { id: 'position_name', label: 'Position', minWidth: 150 },
  { id: 'operational_allowance_paid', label: 'O/T Paid', minWidth: 80 },
  { id: 'meal_allowance_paid', label: 'Meal paid', minWidth: 90 },
  { id: 'meal_allowance', label: 'Meal Rp', minWidth: 80 },
  { id: 'grade_name', label: 'Grading', minWidth: 80 },
];

export default function Tables() {
  const [data, getData] = React.useState([]);
  React.useEffect(() => {
    axios
      .get(API_PATHS.getEmployee, {
        headers: {
          Authorization: 'Bearer ' + Cookies.get(ACCESS_TOKEN_KEY),
        },
      })
      .then((res) => getData(res.data.data.data))
      .catch((err) => console.log(err));
  }, []);

  const rows = data.map((data: any) => {
    const first_contract_date = data.contracts.slice(0, 1);
    const secound_contract_date = data.contracts.slice(1, 2);

    return {
      id: data.staff_id,
      name: data.name,
      gender: data.gender,
      card_number: data.card_number,
      bank_account_no: data.bank_account_no,
      family_card_number: data.family_card_number,
      marriage_code: data.marriage_code,
      mother_name: data.mother_name,
      pob: data.pob,
      dob: data.dob,
      home_address_1: data.home_address_1,
      home_address_2: data.home_address_2,
      nc_id: data.nc_id,
      contract_start_date: data.contract_start_date,
      first_contract_date: first_contract_date.map((item: any) => item.contract_date),
      secound_contract_date: secound_contract_date.map((item: any) => item.contract_date),
      deleted_at: data.deleted_at,
      department_name: data.department_name,
      type: data.type,
      basic_salary: data.basic_salary,
      position_name: data.position_name,
      operational_allowance_paid: data.operational_allowance_paid,
      meal_allowance_paid: data.meal_allowance_paid,
      meal_allowance: data.meal_allowance,
      grade_name: data.grade_name,
    };
  });

  return (
    <Paper sx={{ width: '100%', overflow: 'hidden' }}>
      <TableContainer sx={{ maxHeight: 440 }}>
        <Table stickyHeader aria-label="sticky table">
          <TableHead>
            <TableRow>
              {columns.map((column) => (
                <TableCell key={column.id} style={{ minWidth: column.minWidth }}>
                  {column.label}
                </TableCell>
              ))}
            </TableRow>
          </TableHead>
          <TableBody>
            {data.map((row: any, index) => {
              return (
                <TableRow hover role="checkbox" tabIndex={-1} key={index}>
                  {columns.map((column) => {
                    const value = row[column.id];
                    if (column.id === 'gender' && value === 1) {
                      row[column.id] = 'Male';
                    } else {
                      row[column.id] = 'Female';
                    }

                    return <TableRows key={column.id} data={value} />;
                  })}
                </TableRow>
              );
            })}
          </TableBody>
        </Table>
      </TableContainer>
    </Paper>
  );
}
