import * as React from 'react';
import Paper from '@mui/material/Paper';
import Table from '@mui/material/Table';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import axios from 'axios';
import { API_PATHS } from '../../../../../../configs/api';
import Cookies from 'js-cookie';
import { ACCESS_TOKEN_KEY } from '../../../../../../utils/constants';
import ContainerTableBody from './ContainerTableBody';
import { Column, Employee } from '../../../../../../models/employee';
import Checkbox from '@mui/material/Checkbox';
import ArrowBackIosIcon from '@mui/icons-material/ArrowBackIos';
import ArrowForwardIosIcon from '@mui/icons-material/ArrowForwardIos';
import KeyboardDoubleArrowLeftIcon from '@mui/icons-material/KeyboardDoubleArrowLeft';
import KeyboardDoubleArrowRightIcon from '@mui/icons-material/KeyboardDoubleArrowRight';

import './Table.scss';
import { RESPONSE_STATUS_SUCCESS } from '../../../../../../utils/httpResponseCode';
import { useDispatch } from 'react-redux';
import { ThunkDispatch } from 'redux-thunk';
import { AppState } from '../../../../../../redux/reducer';
import { Action } from 'typesafe-actions';
import { setEmployee } from '../../../../redux/employeeReducer';
import { CircularProgress } from '@mui/material';

const columns: readonly Column[] = [
  { id: 'staff_id', label: 'NIK', minWidth: 85 },
  { id: 'name', label: 'Name', minWidth: 140 },
  { id: 'gender', label: 'Gender', minWidth: 60 },
  { id: 'card_number', label: 'Bank Card No.', minWidth: 120 },
  { id: 'bank_account_no', label: 'Bank Account No.', minWidth: 140 },
  { id: 'family_card_number', label: 'Family Card No.', minWidth: 140 },
  { id: 'marriage_code', label: 'Marriage Status', minWidth: 140 },
  { id: 'mother_name', label: 'Mother Name', minWidth: 140 },
  { id: 'pob', label: 'Place of birth', minWidth: 105 },
  { id: 'dob', label: 'Date of birth', minWidth: 105 },
  { id: 'home_address', label: 'Home Address', minWidth: 660 },
  { id: 'nc_id', label: 'National Card ID No.', minWidth: 150 },
  { id: 'contract_start_date', label: 'Date Start', minWidth: 80 },
  { id: 'first_contract_date', label: 'First Contract', minWidth: 100 },
  { id: 'secound_contract_date', label: 'Secound Contract', minWidth: 120 },
  { id: 'deleted_at', label: 'End Contract', minWidth: 100 },
  { id: 'department_name', label: 'Department', minWidth: 140 },
  { id: 'type', label: 'Employee Type', minWidth: 110 },
  { id: 'basic_salary', label: 'Salary Rp.', minWidth: 80 },
  { id: 'position_name', label: 'Position', minWidth: 140 },
  { id: 'entitle_ot', label: 'O/T Paid', minWidth: 70 },
  { id: 'meal_allowance_paid', label: 'Meal paid', minWidth: 80 },
  { id: 'meal_allowance', label: 'Meal Rp', minWidth: 70 },
  { id: 'grade_name', label: 'Grading', minWidth: 70 },
];

export default function Tables() {
  const [data, getData] = React.useState<Employee[]>([]);
  const [boolCheck, setBoolCheck] = React.useState(false);
  const [loading, setLoading] = React.useState(false);
  const [page, setPage] = React.useState<number>(0);
  const dispatch = useDispatch<ThunkDispatch<AppState, null, Action<string>>>();
  React.useEffect(() => {
    axios
      .get(API_PATHS.getEmployee, {
        headers: {
          Authorization: 'Bearer ' + Cookies.get(ACCESS_TOKEN_KEY),
        },
      })
      .then((res) => getData(res.data.data.data))
      .catch((err) => console.log(err));
  }, []);

  const handleCheckbox = () => {
    setBoolCheck(!boolCheck);
  };

  const getEmployee = React.useCallback(async (page: number) => {
    setLoading(true);
    const json = await axios.get(API_PATHS.getEmployee, {
      headers: {
        Authorization: 'Bearer ' + Cookies.get(ACCESS_TOKEN_KEY),
      },
    });

    setLoading(false);

    if (json?.status === RESPONSE_STATUS_SUCCESS) {
      getData(json.data.data.data);
      console.log(json.data.data);

      return;
    }
  }, []);

  React.useEffect(() => {
    getEmployee(page);
  }, [page, getEmployee]);

  if (loading) {
    return <CircularProgress disableShrink />;
  }

  return (
    <Paper sx={{ width: '100%', overflow: 'hidden', padding: '10px', boxShadow: 'none' }}>
      <div>sdadsa</div>
      <TableContainer sx={{ maxHeight: 525, minHeight: 525 }}>
        <Table stickyHeader aria-label="sticky table">
          <TableHead>
            <TableRow>
              <TableCell>
                <Checkbox checked={boolCheck} onChange={handleCheckbox} color="success" />
              </TableCell>
              {columns.map((column, index) => (
                <TableCell
                  key={index}
                  style={{ minWidth: column.minWidth, textAlign: column.label === 'Home Address' ? 'center' : 'left' }}
                  colSpan={column.label === 'Home Address' ? 2 : 1}
                >
                  {column.label}
                </TableCell>
              ))}
            </TableRow>
          </TableHead>
          <ContainerTableBody data={data.data} columns={columns} check={boolCheck} />
        </Table>
      </TableContainer>
      <hr style={{ opacity: 0.04 }} />
      <div className="d-flex">
        <button className="btn " disabled>
          <KeyboardDoubleArrowLeftIcon />
        </button>
        <button className="btn ">
          <ArrowBackIosIcon />
        </button>
        <button className="btn btn-custom">1</button>
        <button className="btn btn-custom">2</button>
        <button className="btn btn-custom">3</button>
        <button className="btn btn-custom">4</button>
        <button className="btn btn-custom">5</button>
        <button className="btn btn-custom-next">
          <ArrowForwardIosIcon />
        </button>
        <button className="btn btn-custom-next">
          <KeyboardDoubleArrowRightIcon />
        </button>
      </div>
    </Paper>
  );
}
