import { useLocation } from 'react-router';
import { Product } from '../../../models/product';
import { ChangeEventHandler, useCallback, useEffect, useState } from 'react';
import { useDispatch } from 'react-redux';
import { ThunkDispatch } from 'redux-thunk';
import { AppState } from '../../../redux/reducer';
import { Action } from 'typesafe-actions';
import { fetchThunk } from '../../common/redux/thunk';
import { API_PATHS } from '../../../configs/api';
import { RESPONSE_STATUS_SUCCESS } from '../../../utils/httpResponseCode';

function ViewDetail() {
  const { state } = useLocation<Product>();
  const [id, setId] = useState<number>();
  const [data, setData] = useState<Product>();

  const item: Product[] = Object.values(state);

  useEffect(() => {
    item.map((dataItem: Product) => setId(dataItem.id));
  }, []);

  const dispatch = useDispatch<ThunkDispatch<AppState, null, Action<string>>>();
  const getProduct = useCallback(
    async (id: number) => {
      const json = await dispatch(fetchThunk(`${API_PATHS.getProduct}/${id}`, 'get'));

      if (json?.code === RESPONSE_STATUS_SUCCESS) {
        setData(json.data);
        return;
      }
    },
    [dispatch],
  );

  useEffect(() => {
    id && getProduct(id);
  }, [id, getProduct]);

  const onchangeTotal: ChangeEventHandler<HTMLInputElement> = (e) => {
    setData({
      ...data,
      total: Number(e.target.value),
      status: e.target.value,
    });
  };
  console.log(data?.total, data?.status);

  return (
    <div className="card d-flex justify-content-center align-items-center" style={{ width: '100%' }}>
      {data && (
        <div className="card-body" key={data.id}>
          <h5 className="card-title">Product</h5>
          <h6 className="card-subtitle mb-2 text-muted">
            Status: <input type="text" value={data.status} onChange={onchangeTotal} />
          </h6>
          <h6 className="card-subtitle mb-2 text-muted">Client: {data.client ? data.client : 'Không có'}</h6>
          <h6 className="card-subtitle mb-2 text-muted">
            Total: <input type="text" value={data.total} onChange={onchangeTotal} />
          </h6>
          <h6 className="card-subtitle mb-2 text-muted">Currency: {data.currency}</h6>
          <h6 className="card-subtitle mb-2 text-muted">Funding method: {data.fundingMethod}</h6>
          <h6 className="card-subtitle mb-2 text-muted">Order: {data.order}</h6>
          <h6 className="card-subtitle mb-2 text-muted">Invoice: {data.invoice}</h6>
          <h6 className="card-subtitle mb-2 text-muted">Created at: {data.createdAt}</h6>
        </div>
      )}
    </div>
  );
}

export default ViewDetail;
