import axios from 'axios';
import { useEffect, useState } from 'react';
import { Controller, SubmitHandler, useForm } from 'react-hook-form';

interface Country {
  id?: number;
  name: string;
}

function SignUp() {
  const [conutries, getContries] = useState<Country[]>([]);
  useEffect(() => {
    axios
      .get('http://api.training.div3.pgtest.co/api/v1/location')
      .then((res) => getContries(res.data.data))
      .catch((error) => console.log(error));
  }, []);

  const {
    control,
    handleSubmit,
    formState: { errors },
  } = useForm<Country>({
    defaultValues: {
      id: 0,
      name: '',
    },
  });

  //   const onSubmit: SubmitHandler<ILoginParams> = (data) => onLogin(data);

  return (
    // <div>
    //   <input type="text" placeholder="Email" />
    //   <input type="text" placeholder="Password" />
    //   <input type="text" placeholder="Name" />
    //   <input type="text" placeholder="Gioi tinh" />
    //   <select name="cars" id="cars">
    //     <option value="quoc gia">--Quoc gia--</option>
    //     {conutries.map((country) => (
    //       <option key={country.id} value={country.name}>
    //         {country.name}
    //       </option>
    //     ))}
    //   </select>
    // </div>
    <form onSubmit={handleSubmit(onSubmit)} style={{ display: 'flex', flexDirection: 'column' }}>
      <Controller
        name="id"
        control={control}
        rules={{
          required: 'Vui lòng nhập email',
          pattern: {
            value: /^[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,4}$/,
            message: 'Định dạng email không đúng',
          },
        }}
        render={({ field }) => <input {...field} type="text" id="email" />}
      />
    </form>
  );
}

export default SignUp;
