import { Controller, useForm } from 'react-hook-form';
import { ILoginParams } from '../../../../models/auth';
import { FormattedMessage } from 'react-intl';
import './ForgotPasswordForm.scss';

interface Props {
  onLogin(values: ILoginParams): void;
  loading: boolean;
  errorMessage: string;
}

function ForgotPasswordForm(props: Props) {
  const {
    control,
    handleSubmit,
    formState: { errors },
  } = useForm({
    defaultValues: {
      email: '',
    },
  });
  return (
    <form
      onSubmit={handleSubmit((data) => console.log(data))}
      className="row g-3 needs-validation"
      style={{ display: 'flex', flexDirection: 'column' }}
    >
      <h3 className="title" style={{ marginTop: '56px' }}>
        Sign In
      </h3>
      <div className="d-flex flex-column">
        <label htmlFor="inputEmail" className="form-label">
          <FormattedMessage id="email" />
        </label>
        <Controller
          name="email"
          control={control}
          rules={{
            required: true,
            pattern: /^[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,4}$/,
          }}
          render={({ field }) => <input {...field} type="text" id="email" />}
        />
        <span className="text-danger mt-2 d-block " style={{ fontSize: '14px' }}>
          {errors.email?.type === 'required' && <FormattedMessage id="emailRequire" />}
          {errors.email?.type === 'pattern' && <FormattedMessage id="emailInvalid" />}
        </span>
      </div>
      <div className="row justify-content-md-center" style={{ margin: '16px 0' }}>
        <div className="col-md-auto d-flex">
          <button
            className="btn btn-primary me-3"
            type="submit"
            style={{ minWidth: '160px', display: 'flex', alignItems: 'center', justifyContent: 'center' }}
          >
            <div className="spinner-border spinner-border-sm text-light mr-2" role="status" />
            <label htmlFor="inputEmail" className="form-label">
              <FormattedMessage id="register" />
            </label>
          </button>
        </div>
      </div>
    </form>
  );
}

export default ForgotPasswordForm;
