import { Box, Button } from '@mui/material';
import PersonalInformation from './forms/personalInfomation/PersonalInformation';
import Footer from '../footer/Footer';
import { useState } from 'react';
import ContractInformation from './forms/contractInformation/ContractInformation';
import EmploymentDetails from './forms/employmentDetails/EmploymentDetails';
import SalaryNWage from './forms/salaryNwages/SalaryNWage';
import Other from './forms/others/Other';

const tagSvg = (
  <svg width="21" height="20" viewBox="0 0 21 20" fill="none" xmlns="http://www.w3.org/2000/svg">
    <path
      d="M10.4998 18.3337C15.1022 18.3337 18.8332 14.6027 18.8332 10.0003C18.8332 5.39795 15.1022 1.66699 10.4998 1.66699C5.89746 1.66699 2.1665 5.39795 2.1665 10.0003C2.1665 14.6027 5.89746 18.3337 10.4998 18.3337Z"
      stroke="#FFB7B9"
      strokeWidth="2"
      strokeLinecap="round"
      strokeLinejoin="round"
    ></path>
    <path
      d="M10.5 6.66699V10.0003"
      stroke="#FFB7B9"
      strokeWidth="2"
      strokeLinecap="round"
      strokeLinejoin="round"
    ></path>
    <path d="M10.5 13.333H10.5083" stroke="#FFB7B9" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"></path>
  </svg>
);

const options = [
  {
    title: 'Employee Infomation',
    name: 0,
    svg: tagSvg,
    colorButton: 'rgb(237, 246, 255)',
  },
  {
    title: 'Contract Information',
    name: 1,
    svg: tagSvg,
    colorButton: 'rgb(237, 246, 255)',
  },
  {
    title: 'Employment Details',
    name: 2,
  },
  {
    title: 'Salary & Wages',
    name: 3,
  },
  {
    title: 'Others',
    name: 4,
  },
];

const styleButton = {
  textTransform: 'none',
  padding: '6px 16px',
  minWidth: '180px',
  height: '42px',
  fontWeight: '400',
  fontFamily: 'SVN-Sofia-Regular',
  fontSize: '14px',
  backgroundColor: 'rgb(237, 246, 255)',
  color: 'rgb(0, 145, 255)',
  boxShadow: 'none',
  '&:hover': { backgroundColor: 'rgba(0, 145, 255, 0.08)', boxShadow: 'none' },
  '&:focus': { backgroundColor: 'rgb(0, 145, 255)', color: '#fff' },
};

function Option() {
  const [component, setComponent] = useState<number>(0);
  const [bool, setBool] = useState<boolean>(true);
  const handleOptionClick = (name: number) => {
    setComponent(name);
    setBool(false); // Set bool to true if name is 1, otherwise set to false
  };
  console.log(component);

  return (
    <Box>
      <Box sx={{ marginBottom: '16px', gap: '4px', display: 'flex', flexFlow: 'row wrap' }}>
        <Button variant="contained" sx={styleButton} autoFocus>
          Employee Infomation
          {/* {text.svg && text.svg} */}
        </Button>
        <Button variant="contained" sx={styleButton}>
          Contract Information
        </Button>
        <Button variant="contained" sx={styleButton}>
          Employment Details
        </Button>
        <Button variant="contained" sx={styleButton}>
          Salary & Wages
        </Button>
        <Button variant="contained" sx={styleButton}>
          Others
        </Button>
      </Box>
      <PersonalInformation bool={!bool && component === 0} />
      <ContractInformation bool={!bool && component === 1} />
      <EmploymentDetails bool={!bool && component === 2} />
      <SalaryNWage bool={!bool && component === 3} />
      <Other bool={!bool && component === 4} />
    </Box>
  );
}

export default Option;
