import { Grid, Stack, Typography } from '@mui/material';
import Selected from '../../../selected/Selected';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import { ThemeProvider, createTheme } from '@mui/material/styles';

const theme = createTheme({
  components: {
    MuiCheckbox: {
      styleOverrides: {
        root: {
          color: 'rgb(48, 164, 108)',
        },
      },
    },
    MuiSvgIcon: {
      styleOverrides: {
        root: {
          width: '24px',
          height: '24px',
        },
      },
    },
    MuiTypography: {
      styleOverrides: {
        root: {
          fontFamily: 'SVN-Sofia-Regular',
        },
      },
    },
    MuiFormControlLabel: {
      styleOverrides: {
        root: {
          marginLeft: '-11px !important',
        },
      },
    },
  },
});

const svg = (
  <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg" fontSize="medium">
    <rect x="2" y="2" width="20" height="20" rx="4" fill="white"></rect>
    <rect x="2" y="2" width="20" height="20" rx="4" stroke="#D7DBDF"></rect>
  </svg>
);

function EmploymentDetailsForm() {
  return (
    <Stack
      component="form"
      direction="column"
      spacing={2}
      sx={{
        padding: '0 20px 20px 20px',
        gap: '10px',
        display: 'flex',
        maxWidth: '560px',
        width: '100%',
      }}
    >
      <Grid container spacing={'row'} sx={{ '&:first-of-type': { marginLeft: '0' }, marginTop: '0 !important' }}>
        <Grid
          item
          xs={12}
          md={5}
          xl={4}
          sx={{
            paddingLeft: '0 !important',
            paddingTop: '0 !important',
            display: 'flex',
            alignItems: 'center',
            marginTop: '0 !important',
          }}
        >
          <Typography
            variant="body2"
            gutterBottom
            sx={{
              fontFamily: 'SVN-Sofia-Regular',
              display: 'flex',
              alignItems: 'center',
              margin: 0,
              fontSize: '16px',
            }}
          >
            Department
          </Typography>
        </Grid>
        <Grid item xs={12} md={7} xl={8} sx={{ paddingLeft: '0 !important', paddingTop: '0 !important' }}>
          <Selected
            defaultOption="Choose Department"
            objectOption={['N/A', 'Developer', 'Quanlity Controjk', 'Maintenance', 'Business Developer']}
          />
        </Grid>
      </Grid>
      <Grid
        container
        spacing={'row'}
        sx={{ '&:first-of-type': { marginLeft: '0', marginTop: 0 }, marginTop: '0 !important' }}
      >
        <Grid
          item
          xs={12}
          md={5}
          xl={4}
          sx={{
            paddingLeft: '0 !important',
            paddingTop: '0 !important',
            display: 'flex',
            alignItems: 'center',
            marginTop: '0 !important',
          }}
        >
          <Typography
            variant="body2"
            gutterBottom
            sx={{
              fontFamily: 'SVN-Sofia-Regular',
              display: 'flex',
              alignItems: 'center',
              margin: 0,
              fontSize: '16px',
            }}
          >
            Position
          </Typography>
        </Grid>
        <Grid item xs={12} md={7} xl={8} sx={{ paddingLeft: '0 !important', paddingTop: '0 !important' }}>
          <Selected defaultOption="Choose Position" objectOption={['N/A', 'Junior', 'Vice Manager', 'Manager']} />
        </Grid>
      </Grid>
      <ThemeProvider theme={theme}>
        <FormControlLabel
          control={
            <Checkbox
              icon={svg}
              sx={{
                '&.Mui-checked': {
                  color: 'rgb(48, 164, 108)',
                },
              }}
            />
          }
          label="Entitled OT"
        />
        <FormControlLabel
          control={
            <Checkbox
              icon={svg}
              sx={{
                '&.Mui-checked': {
                  color: 'rgb(48, 164, 108)',
                },
              }}
            />
          }
          label="Meal Allowance Paid"
        />
        <FormControlLabel
          control={<Checkbox icon={svg} defaultChecked disabled />}
          label="Operational Allowance Paid"
        />
        <FormControlLabel control={<Checkbox icon={svg} defaultChecked disabled />} label="Attendance Allowance Paid" />
      </ThemeProvider>
    </Stack>
  );
}

export default EmploymentDetailsForm;
