import { Box, FormControl, InputBase, MenuItem } from '@mui/material';
import Select from '@mui/material/Select';
import '../../../../LoginPage.scss';

const input = (
  <InputBase sx={{ backgroundColor: 'rgb(241, 243, 245)', input: { padding: '12px' }, borderRadius: '6px' }} />
);

const styled = { fontSize: '16px', fontFamily: 'SVN-Sofia-Regular', fontStyle: 'normal', color: '#000' };

export const fields1 = [
  {
    field: 'Name',
    required: '*',
    input: input,
  },
  {
    field: 'Gender',
    required: '*',
    input: (
      <div className="col-12">
        <Box sx={{ minWidth: 120 }}>
          <FormControl fullWidth>
            <Select
              defaultValue=""
              // onChange={handleChange}
              inputProps={{ 'aria-label': 'Without label' }}
              style={{ background: '#f1f3f5' }}
              displayEmpty
              className="ip"
            >
              <MenuItem value="" id="menu-item-1" disabled>
                <em
                  style={{
                    fontSize: '16px',
                    fontFamily: 'SVN-Sofia-Regular',
                    fontStyle: 'normal',
                    color: 'rgb(104, 112, 118)',
                  }}
                >
                  Choose Gender
                </em>
              </MenuItem>
              <MenuItem sx={styled} className="menu-item" value={1}>
                Male
              </MenuItem>
              <MenuItem sx={styled} className="menu-item" value={2}>
                Female
              </MenuItem>
            </Select>
          </FormControl>
        </Box>
        {/* {!!validate?.factory && (
      <small className="text-danger">
        <FormattedMessage id={validate?.factory} />
      </small>
    )} */}
      </div>
    ),
  },
  {
    field: 'Mother Name',
    input: input,
  },
  {
    field: 'Date of birth',
    input: input,
    required: '*',
  },
  {
    field: 'Place of birth',
    input: input,
  },
  {
    field: 'KTP No.',
    input: input,
    required: '*',
  },
  {
    field: 'Natoinal Card ID',
    input: input,
    required: '*',
  },
  {
    field: 'Home Address 1',
    input: input,
  },
  {
    field: 'Home Address 2',
    input: input,
  },
];

export const fields2 = [
  {
    field: 'Mobile No.',
    input: input,
  },
  {
    field: 'Tel No.',
    input: input,
  },
  {
    field: 'Marriage Status',
    input: (
      <div className="col-12">
        <Box sx={{ minWidth: 120 }}>
          <FormControl fullWidth>
            <Select
              defaultValue=""
              // onChange={handleChange}
              inputProps={{ 'aria-label': 'Without label' }}
              style={{ background: '#f1f3f5' }}
              displayEmpty
              className="ip"
            >
              <MenuItem value="" id="menu-item-1" disabled>
                <em style={styled}>Choose Marriage Status</em>
              </MenuItem>
              <MenuItem sx={styled} className="menu-item" value={1}>
                N/A
              </MenuItem>
              <MenuItem sx={styled} className="menu-item" value={2}>
                Married with 1 kid
              </MenuItem>
              <MenuItem sx={styled} className="menu-item" value={3}>
                Single
              </MenuItem>
              <MenuItem sx={styled} className="menu-item" value={4}>
                Married
              </MenuItem>
            </Select>
          </FormControl>
        </Box>
        {/* {!!validate?.factory && (
      <small className="text-danger">
        <FormattedMessage id={validate?.factory} />
      </small>
    )} */}
      </div>
    ),
  },
  {
    field: 'Bank Card No.',
    input: input,
  },
  {
    field: 'Bank Account No.',
    input: input,
  },
  {
    field: 'Bank Name',
    input: input,
  },
  {
    field: 'Family Card Number',
    input: input,
  },
  {
    field: 'Safety Insurance No.',
    input: input,
  },
  {
    field: 'Health Insurance No.',
    input: input,
  },
];
