import { ActionType, createCustomAction, getType } from 'typesafe-actions';
import { Employee } from '../../../models/employee';

export interface ProductState {
  data?: any;
  //   searchProduct?: SearchDataType;
  title?: string;
  employee?: any;
}

export const setEmployee = createCustomAction('getEmployee', (data: any) => ({
  data,
}));

export const setEmployeeAdd = createCustomAction('setEmployee', (data: any) => ({
  data,
}));

// export const updateProduct = createCustomAction('public/updateProduct', (data: UpdateProduct) => ({
//   data,
// }));

export const setTitle = createCustomAction('getTitle', (data: string) => ({
  data,
}));

const actions = { setEmployee, setTitle, setEmployeeAdd };

type Action = ActionType<typeof actions>;

export default function reducer(state: ProductState = {}, action: Action) {
  switch (action.type) {
    case getType(setEmployee):
      return { ...state, data: action.data };
    case getType(setEmployeeAdd):
      return { ...state, employee: action.data };
    // case getType(updateProduct):
    //   return { ...state, data: action.data };
    case getType(setTitle):
      return { ...state, title: action.data };
    default:
      return state;
  }
}
