import React, { useState } from 'react';
import logo from '../../../imagies/Rectangle4.png';
import { ILoginParams } from '../../../models/auth';
import { useDispatch } from 'react-redux';
import { ThunkDispatch } from 'redux-thunk';
import { AppState } from '../../../redux/reducer';
import { Action } from 'redux';
import { API_PATHS } from '../../../configs/api';
import { RESPONSE_STATUS_SUCCESS } from '../../../utils/httpResponseCode';
import { setUserInfo } from '../redux/authReducer';
import Cookies from 'js-cookie';
import { ACCESS_TOKEN_KEY } from '../../../utils/constants';
import { ROUTES } from '../../../configs/routes';
import { replace } from 'connected-react-router';
import { getErrorMessageResponse } from '../../../utils';
import LoginForm from '../components/LoginForm';
import './LoginPage.scss';
import axios from 'axios';

import { Alert } from '@mui/material';
import Snackbar from '@mui/material/Snackbar';

const LoginPage = () => {
  const dispatch = useDispatch<ThunkDispatch<AppState, null, Action<string>>>();
  const [loading, setLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');

  const [errorSnackbarOpen, setErrorSnackbarOpen] = useState(false);
  const [errorSnackbarMessage, setErrorSnackbarMessage] = useState('');

  React.useEffect(() => {
    if (loading) {
      setErrorSnackbarOpen(true);
    }
    setLoading(false);
  }, [loading]);

  const onLogin = async (values: ILoginParams) => {
    setErrorMessage('');
    setLoading(true);

    const json = await axios.post(API_PATHS.signIn, {
      username: values.username,
      password: values.password,
      company_id: values.company_id,
    });

    setLoading(false);

    if (json?.status === RESPONSE_STATUS_SUCCESS) {
      dispatch(setUserInfo(json.data));
      Cookies.set(ACCESS_TOKEN_KEY, json.data.data.token);
      dispatch(replace(ROUTES.employee));
      return;
    } else {
      setErrorSnackbarOpen(true);
      setErrorSnackbarMessage(getErrorMessageResponse(json));
    }
  };

  return (
    <div
      style={{
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        flexDirection: 'column',
        background: '#f8f9fa',
      }}
    >
      {/* <Snackbar
        open={errorSnackbarOpen}
        autoHideDuration={3000}
        onClose={() => setErrorSnackbarOpen(false)}
        anchorOrigin={{ vertical: 'top', horizontal: 'right' }}
      >
        <Alert severity="error">Incorrect Username, Password or Factory. Please try again!</Alert>
      </Snackbar> */}
      <Snackbar
        open={errorSnackbarOpen}
        autoHideDuration={6000}
        onClose={() => setErrorSnackbarOpen(false)}
        message={errorSnackbarMessage}
        anchorOrigin={{ vertical: 'top', horizontal: 'center' }}
      />
      <img src={logo} alt="" style={{ width: '100px', height: '100px', marginTop: '64px' }} />
      <h3 className="title">HR Management System</h3>

      <LoginForm onLogin={onLogin} loading={loading} errorMessage={errorMessage} />

      <span className="footer">Copyright © 2022. All Rights Reserved</span>
    </div>
  );
};

export default LoginPage;
