import { Box, FormControl, InputBase, MenuItem } from '@mui/material';
import Select from '@mui/material/Select';
import '../../../../../LoginPage.scss';
import Selected from '../../../selected/Selected';

const input = (
  <InputBase
    sx={{ backgroundColor: 'rgb(241, 243, 245)', input: { padding: '12px' }, borderRadius: '6px', width: '100%' }}
  />
);

const styled = { fontFamily: 'SVN-Sofia-Regular' };

export const fields1 = [
  {
    field: 'Name',
    required: '*',
    input: input,
  },
  {
    field: 'Gender',
    required: '*',
    input: (
      <div className="col-12">
        <Selected defaultOption="Choose Gender" objectOption={['Male', 'Female']} />
        {/* {!!validate?.factory && (
      <small className="text-danger">
        <FormattedMessage id={validate?.factory} />
      </small>
    )} */}
      </div>
    ),
  },
  {
    field: 'Mother Name',
    input: input,
  },
  {
    field: 'Date of birth',
    required: '*',
  },
  {
    field: 'Place of birth',
    input: input,
  },
  {
    field: 'KTP No.',
    input: input,
    required: '*',
  },
  {
    field: 'Natoinal Card ID',
    input: input,
    required: '*',
  },
  {
    field: 'Home Address 1',
    input: input,
  },
  {
    field: 'Home Address 2',
    input: input,
  },
];

export const fields2 = [
  {
    field: 'Mobile No.',
    input: input,
  },
  {
    field: 'Tel No.',
    input: input,
  },
  {
    field: 'Marriage Status',
    input: (
      <div className="col-12">
        <Selected
          defaultOption="Choose Marriage Status"
          objectOption={['N/A', 'Married with 1 kid', 'Single', 'Married']}
        />

        {/* {!!validate?.factory && (
      <small className="text-danger">
        <FormattedMessage id={validate?.factory} />
      </small>
    )} */}
      </div>
    ),
  },
  {
    field: 'Bank Card No.',
    input: input,
  },
  {
    field: 'Bank Account No.',
    input: input,
  },
  {
    field: 'Bank Name',
    input: input,
  },
  {
    field: 'Family Card Number',
    input: input,
  },
  {
    field: 'Safety Insurance No.',
    input: input,
  },
  {
    field: 'Health Insurance No.',
    input: input,
  },
];
