import { Checkbox, TableCell, TableRow } from '@mui/material';
import { useEffect, useState } from 'react';

interface Props {
  checked: boolean;
  columns: any;
  row: any;
}

function Rows(props: Props) {
  const { checked, columns, row } = props;
  const [isChecked, setIsChecked] = useState<boolean>(checked);

  useEffect(() => {
    setIsChecked(checked);
  }, [checked]);

  const handleCheck = (event: React.ChangeEvent<HTMLInputElement>) => {
    setIsChecked(event.target.checked);
  };

  const handleChecked = () => {
    setIsChecked(!isChecked);
  };

  return (
    <TableRow
      hover
      role="checkbox"
      tabIndex={-1}
      onClick={handleChecked}
      sx={{ backgroundColor: isChecked ? 'rgb(233, 249, 238)' : 'rgb(248, 249, 250)' }}
    >
      <TableCell>
        <Checkbox checked={isChecked} onChange={handleCheck} color="success" />
      </TableCell>
      {columns.map((column: any) => {
        const value = row[column.id];

        return (
          <TableCell
            key={column.id}
            colSpan={column.id[0] === 'home_address_1' && column.id[1] === 'home_address_2' ? 2 : 1}
          >
            {column.id[0] === 'home_address_1' && column.id[1] === 'home_address_2'
              ? `${row.home_address_1}, ${row.home_address_2}`
              : value}
          </TableCell>
        );
      })}
    </TableRow>
  );
}

export default Rows;
