import { Box, Grid, Stack, Typography } from '@mui/material';
import { fields1, fields2 } from './fileds';
import InputDateTime from '../InputDateTime';
import { FormEvent, memo } from 'react';

function PersonalInformationForm() {
  const hanldeSunmit = (e: FormEvent<HTMLFormElement>) => {
    e.preventDefault();
  };
  return (
    <Box
      component="form"
      onSubmit={hanldeSunmit}
      sx={{ gap: '20px', display: 'flex', flexWrap: 'wrap', marginBottom: '20px' }}
    >
      {/* <Box sx={{ display: 'flex', flexWrap: 'wrap', marginBottom: '20px' }}> */}
      <Stack
        direction="column"
        spacing={2}
        sx={{ flex: '1 1 0%', paddingLeft: '20px', paddingRight: '20px', gap: '10px' }}
      >
        {fields1.map((item: any, index: number) => (
          <Grid
            container
            spacing={2}
            key={index}
            sx={{ '&:first-of-type': { marginLeft: '0', marginTop: 0 }, marginTop: '0 !important' }}
          >
            <Grid
              item
              xs={12}
              md={5}
              sx={{
                paddingLeft: '0 !important',
                paddingTop: '0 !important',
                display: 'flex',
                alignItems: 'center',
                marginTop: '0 !important',
              }}
            >
              <Typography
                variant="body2"
                gutterBottom
                sx={{
                  fontFamily: 'SVN-Sofia-Regular',
                  display: 'flex',
                  alignItems: 'center',
                  margin: 0,
                  fontSize: '16px',
                }}
              >
                {item.field}
                <Box component="span" sx={{ color: 'rgb(229, 72, 77)', margin: 0 }}>
                  {item.required}
                </Box>
              </Typography>
            </Grid>
            <Grid item xs={12} md={7} sx={{ paddingLeft: '0 !important', paddingTop: '0 !important' }}>
              {item.field === 'Date of birth' ? <InputDateTime /> : item.input}
            </Grid>
          </Grid>
        ))}
      </Stack>
      <Stack
        direction="column"
        spacing={2}
        sx={{ flex: '1 1 0%', paddingLeft: '20px', paddingRight: '20px', gap: '10px' }}
      >
        {fields2.map((item: any, index: number) => (
          <Grid
            container
            spacing={2}
            key={index}
            sx={{ '&:first-of-type': { marginLeft: '0', marginTop: 0 }, marginTop: '0 !important' }}
          >
            <Grid
              item
              xs={12}
              md={5}
              sx={{
                paddingLeft: '0 !important',
                paddingTop: '0 !important',
                display: 'flex',
                alignItems: 'center',
                marginTop: '0 !important',
              }}
            >
              <Typography
                variant="body2"
                gutterBottom
                sx={{
                  fontFamily: 'SVN-Sofia-Regular',
                  display: 'flex',
                  alignItems: 'center',
                  margin: 0,
                  fontSize: '16px',
                }}
              >
                {item.field}
                <Box component="span" sx={{ color: 'rgb(229, 72, 77)', margin: 0 }}>
                  {item.required}
                </Box>
              </Typography>
            </Grid>
            <Grid item xs={12} md={7} sx={{ paddingLeft: '0 !important', paddingTop: '0 !important' }}>
              {item.input}
            </Grid>
          </Grid>
        ))}
      </Stack>
      {/* </Box> */}
    </Box>
  );
}

export default memo(PersonalInformationForm);
