import { useLocation } from 'react-router';
import { Product } from '../../../models/product';

function ViewDetail() {
  let { state } = useLocation<Product>();

  const item: Product[] = Object.values(state);
  console.log(item);

  //   const { status } = state;
  //   console.log(status);
  //   console.log(state.status);

  return (
    <div className="card d-flex justify-content-center align-items-center" style={{ width: '100%' }}>
      <div className="card-body">
        {item.map((data: Product) => (
          <div>
            <h5 className="card-title">Product</h5>
            <h6 className="card-subtitle mb-2 text-muted">Status: {data.status}</h6>
            <p className="card-text">
              Some quick example text to build on the card title and make up the bulk of the card's content.
            </p>
            <a href="#" className="card-link">
              Card link
            </a>
            <a href="#" className="card-link">
              Another link
            </a>
          </div>
        ))}
      </div>
    </div>
  );
}

export default ViewDetail;
