import { Box, Grid, InputBase, Stack, Typography } from '@mui/material';
import { fields2 } from '../fileds';
import { FormattedMessage } from 'react-intl';
import Selected from '../../../../selected/Selected';

function InfoForm2() {
  return (
    <Stack
      direction="column"
      spacing={2}
      sx={{ flex: '1 1 0%', paddingLeft: '20px', paddingRight: '20px', gap: '10px' }}
    >
      {fields2.map((item: any, index: number) => (
        <Grid
          container
          spacing={2}
          key={index}
          sx={{
            '&:first-of-type': { marginLeft: '0', marginTop: 0 },
            marginTop: '0 !important',
            marginLeft: '-16px !important',
          }}
        >
          <Grid
            item
            xs={12}
            md={5}
            xl={4}
            sx={{
              paddingLeft: '0 !important',
              paddingTop: '0 !important',
              display: 'flex',
              alignItems: 'center',
              marginTop: '0 !important',
            }}
          >
            <Typography
              variant="body2"
              gutterBottom
              sx={{
                fontFamily: 'SVN-Sofia-Regular',
                display: 'flex',
                alignItems: 'center',
                margin: 0,
                fontSize: '16px',
              }}
            >
              <FormattedMessage id={item.field} />
              <Box component="span" sx={{ color: 'rgb(229, 72, 77)', margin: 0 }}>
                {item.required}
              </Box>
            </Typography>
          </Grid>
          <Grid item xs={12} md={7} xl={8} sx={{ paddingLeft: '0 !important', paddingTop: '0 !important' }}>
            {item.input === 'input' && (
              <InputBase
                sx={{
                  backgroundColor: 'rgb(241, 243, 245)',
                  input: { padding: '12px' },
                  borderRadius: '6px',
                  width: '100%',
                }}
              />
            )}
            {item.input === 'selected' && (
              <Selected
                defaultOption="Choose Marriage Status"
                objectOption={['N/A', 'Married with 1 kid', 'Single', 'Married']}
              />
            )}
          </Grid>
        </Grid>
      ))}
    </Stack>
  );
}

export default InfoForm2;
