import { DataGrid, GridColDef } from '@mui/x-data-grid';
import axios from 'axios';
import { useEffect, useState } from 'react';
import { API_PATHS } from '../../../../../../configs/api';
import Cookies from 'js-cookie';
import { ACCESS_TOKEN_KEY } from '../../../../../../utils/constants';

import './Table.scss';

const columns: GridColDef[] = [
  { field: 'id', headerName: 'NIK', width: 95 },
  { field: 'name', headerName: 'Name', width: 150 },
  { field: 'gender', headerName: 'Gender', width: 70 },
  { field: 'card_number', headerName: 'Bank Card No.', width: 130 },
  { field: 'bank_account_no', headerName: 'Bank Account No.', width: 150 },
  { field: 'family_card_number', headerName: 'Family Card No.', width: 150 },
  { field: 'marriage_code', headerName: 'Marriage Status', width: 130 },
  { field: 'mother_name', headerName: 'Mother Name', width: 150 },
  { field: 'pob', headerName: 'Place of birth', width: 115 },
  { field: 'dob', headerName: 'Date of birth', width: 115 },
  { field: 'home_address_1', headerName: 'Home Address', width: 350 },
  { field: 'home_address_2', headerName: 'Home Address', width: 350 },
  { field: 'nc_id', headerName: 'National Card ID No.', width: 170 },
  { field: 'contract_start_date', headerName: 'Date Start', width: 90 },
  { field: 'first_contract_date', headerName: 'First Contract', width: 110 },
  { field: 'secound_contract_date', headerName: 'Secound Contract', width: 130 },
  { field: 'deleted_at', headerName: 'End Contract', width: 110 },
  { field: 'department_name', headerName: 'Department', width: 150 },
  { field: 'type', headerName: 'Employee Type', width: 120 },
  { field: 'basic_salary', headerName: 'Salary Rp.', width: 90 },
  { field: 'position_name', headerName: 'Position', width: 150 },
  { field: 'operational_allowance_paid', headerName: 'O/T Paid', width: 80 },
  { field: 'meal_allowance_paid', headerName: 'Meal paid', width: 90 },
  { field: 'meal_allowance', headerName: 'Meal Rp', width: 80 },
  { field: 'grade_name', headerName: 'Grading', width: 80 },
];

export default function Table() {
  const [data, getData] = useState([]);
  useEffect(() => {
    axios
      .get(API_PATHS.getEmployee, {
        headers: {
          Authorization: 'Bearer ' + Cookies.get(ACCESS_TOKEN_KEY),
        },
      })
      .then((res) => getData(res.data.data.data))
      .catch((err) => console.log(err));
  }, []);

  console.log(data);

  const row = data.map((data: any) => {
    const first_contract_date = data.contracts.slice(0, 1);
    const secound_contract_date = data.contracts.slice(1, 2);

    return {
      id: data.staff_id,
      name: data.name,
      gender: data.gender,
      card_number: data.card_number,
      bank_account_no: data.bank_account_no,
      family_card_number: data.family_card_number,
      marriage_code: data.marriage_code,
      mother_name: data.mother_name,
      pob: data.pob,
      dob: data.dob,
      home_address_1: data.home_address_1,
      home_address2: data.home_address_2,
      nc_id: data.nc_id,
      contract_start_date: data.contract_start_date,
      first_contract_date: first_contract_date.map((item: any) => item.contract_date),
      secound_contract_date: secound_contract_date.map((item: any) => item.contract_date),
      deleted_at: data.deleted_at,
      department_name: data.department_name,
      type: data.type,
      basic_salary: data.basic_salary,
      position_name: data.position_name,
      operational_allowance_paid: data.operational_allowance_paid,
      meal_allowance_paid: data.meal_allowance_paid,
      meal_allowance: data.meal_allowance,
      grade_name: data.grade_name,
    };
  });

  console.log(row);

  return (
    <div style={{ height: 528, width: '100%', borderRadius: 12, background: '#fff' }}>
      <DataGrid
        rows={row}
        columns={columns}
        initialState={{
          pagination: {
            paginationModel: { page: 0, pageSize: 25 },
          },
        }}
        checkboxSelection
      />
    </div>
  );
}
