import React, { useState } from 'react';
import logo from '../../../imagies/Rectangle4.png';
import { ILoginParams } from '../../../models/auth';
import { useDispatch } from 'react-redux';
import { ThunkDispatch } from 'redux-thunk';
import { AppState } from '../../../redux/reducer';
import { Action } from 'redux';
import { fetchThunk } from '../../common/redux/thunk';
import { API_PATHS } from '../../../configs/api';
import { RESPONSE_STATUS_SUCCESS } from '../../../utils/httpResponseCode';
import { setUserInfo } from '../redux/authReducer';
import Cookies from 'js-cookie';
import { ACCESS_TOKEN_KEY } from '../../../utils/constants';
import { ROUTES } from '../../../configs/routes';
import { replace } from 'connected-react-router';
import { getErrorMessageResponse } from '../../../utils';
import LoginForm from '../components/LoginForm';
import './LoginPage.scss';

const LoginPage = () => {
  const dispatch = useDispatch<ThunkDispatch<AppState, null, Action<string>>>();
  const [loading, setLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');

  const onLogin = React.useCallback(
    async (values: ILoginParams) => {
      setErrorMessage('');
      setLoading(true);

      const json = await dispatch(
        fetchThunk('https://api-training.hrm.div4.pgtest.co/api/v1/login', 'post', {
          username: values.username,
          password: values.password,
          company_id: values.company_id,
        }),
      );

      setLoading(false);

      if (json?.code === RESPONSE_STATUS_SUCCESS) {
        dispatch(setUserInfo(json.data));
        Cookies.set(ACCESS_TOKEN_KEY, json.data.token);
        dispatch(replace(ROUTES.home));
        return;
      }

      // setErrorMessage(getErrorMessageResponse(json));
    },
    [dispatch],
  );

  return (
    <div
      style={{
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        flexDirection: 'column',
        background: '#f8f9fa',
      }}
    >
      <img src={logo} alt="" style={{ width: '100px', height: '100px', marginTop: '64px' }} />
      <h3 className="title">HR Management System</h3>

      <LoginForm onLogin={onLogin} loading={loading} errorMessage={errorMessage} />

      <span className="footer">Copyright Â© 2022. All Rights Reserved</span>
    </div>
  );
};

export default LoginPage;
