import { InputBase } from '@mui/material';
import { FormattedMessage } from 'react-intl';

interface Props {
  validate?: any;
  validateForm: any;
  setValidateForm: any;
  type?: string;
  handleBlur(values: any): void;
  value: any;
}

function FieldInput({ validate, validateForm, setValidateForm, type = 'text', handleBlur, value }: Props) {
  return (
    <div>
      <InputBase
        sx={{
          backgroundColor: !!validate ? 'rgb(255, 239, 239)' : 'rgb(241, 243, 245)',
          border: !!validate ? '1px solid rgb(243, 174, 175)' : 'none',
          input: { padding: '12px' },
          borderRadius: '6px',
          width: '100%',
        }}
        value={value}
        onChange={(e) => {
          setValidateForm({ ...validateForm, validate: e.target.value });
        }}
        type={type}
        onBlur={handleBlur}
      />
      {!!validate && (
        <small className="text-danger">
          <FormattedMessage id={validate} />
        </small>
      )}
    </div>
  );
}

export default FieldInput;
