import React from 'react';
import { FormattedMessage } from 'react-intl';
import { ILoginParams, ILoginValidation } from '../../../models/auth';
import { validateLogin, validLogin } from '../utils';

import '../pages/LoginPage.scss';

import Box from '@mui/material/Box';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select, { SelectChangeEvent } from '@mui/material/Select';
import { replace } from 'connected-react-router';
import { ROUTES } from '../../../configs/routes';
import OutlinedInput from '@mui/material/OutlinedInput';

import { useDispatch } from 'react-redux';
import { ThunkDispatch } from 'redux-thunk';
import { AppState } from '../../../redux/reducer';
import { Action } from 'typesafe-actions';
interface Props {
  onLogin(values: ILoginParams): void;
  loading: boolean;
  errorMessage: string;
}

const factory = [
  { id: 1, value: 'SBM' },
  { id: 2, value: 'DMF' },
];

const LoginForm = (props: Props) => {
  const { onLogin, loading, errorMessage } = props;

  const [formValues, setFormValues] = React.useState<ILoginParams>({ username: '', password: '', company_id: 0 });
  const [validate, setValidate] = React.useState<ILoginValidation>();

  const onSubmit = React.useCallback(() => {
    const validate = validateLogin(formValues);

    setValidate(validate);

    if (!validLogin(validate)) {
      return;
    }

    onLogin(formValues);
  }, [formValues, onLogin]);

  const dispatch = useDispatch<ThunkDispatch<AppState, null, Action<string>>>();
  const handlePage = () => {
    dispatch(replace(ROUTES.forgotPassword));
  };

  const handleChange = (event: SelectChangeEvent) => {
    setFormValues({ ...formValues, company_id: event.target.value });
  };

  return (
    <form
      style={{ maxWidth: '560px', width: '100%', display: 'flex', justifyContent: 'center' }}
      noValidate
      onSubmit={(e) => {
        e.preventDefault();
        onSubmit();
      }}
      className="row g-3 needs-validation"
    >
      <h3 className="title" style={{ marginTop: '56px' }}>
        Sign In
      </h3>
      <div className="sign-in">
        {!!errorMessage && (
          <div className="alert alert-danger" role="alert" style={{ width: '100%' }}>
            {errorMessage}
          </div>
        )}

        <div className="col-md-12">
          <label htmlFor="inputEmail" className="form-label mt-2">
            <FormattedMessage id="email" />
          </label>
          <input
            type="text"
            className="form-control ip"
            id="inputEmail"
            style={{ background: '#f1f3f5', padding: '12px' }}
            value={formValues.username}
            onChange={(e) => setFormValues({ ...formValues, username: e.target.value })}
          />

          {!!validate?.username && (
            <small className="text-danger">
              <FormattedMessage id={validate?.username} />
            </small>
          )}
        </div>

        <div className="col-md-12">
          <label htmlFor="inputPassword" className="form-label mt-2">
            <FormattedMessage id="password" />
          </label>
          <input
            type="password"
            className="form-control ip"
            id="inputPassword"
            style={{ background: '#f1f3f5', padding: '12px' }}
            value={formValues.password}
            onChange={(e) => setFormValues({ ...formValues, password: e.target.value })}
          />

          {!!validate?.password && (
            <small className="text-danger">
              <FormattedMessage id={validate?.password} />
            </small>
          )}
        </div>

        <div className="col-12">
          <FormControl sx={{ m: 1, minWidth: 120 }}>
            <Select
              value={formValues.company_id}
              onChange={handleChange}
              displayEmpty
              inputProps={{ 'aria-label': 'Without label' }}
            >
              <MenuItem value="">
                <em>asdasdas</em>
              </MenuItem>
              <MenuItem value={1}>Ten</MenuItem>
              <MenuItem value={2}>Twenty</MenuItem>
            </Select>
          </FormControl>
          {!!validate?.factory && (
            <small className="text-danger">
              <FormattedMessage id={validate?.factory} />
            </small>
          )}
        </div>

        <div className="row justify-content-md-center" style={{ margin: '42px 0 12px 0' }}>
          <div className="col-md-auto" style={{ width: '100%', padding: 0 }}>
            <button
              className="btn btn-lg"
              type="submit"
              style={{
                width: '100%',
                fontSize: 16,
                padding: 12,
                background: '#0091ff',
                color: '#fff',
              }}
              disabled={loading}
            >
              {loading && <div className="spinner-border spinner-border-sm text-light mr-2" role="status" />}
              <FormattedMessage id="register" />
            </button>
          </div>
        </div>
        <div onClick={handlePage}>
          <span
            className="d-flex justify-content-center forgot"
            style={{ cursor: 'pointer', color: '#0091FF', fontSize: 14 }}
          >
            Forgot Your Password?
          </span>
        </div>
      </div>
    </form>
  );
};

export default LoginForm;
