import * as React from 'react';
import { Theme, useTheme } from '@mui/material/styles';
import Box from '@mui/material/Box';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select, { SelectChangeEvent } from '@mui/material/Select';
import Chip from '@mui/material/Chip';
import axios from 'axios';
import { API_PATHS } from '../../../../../../../../configs/api';
import { RESPONSE_STATUS_SUCCESS } from '../../../../../../../../utils/httpResponseCode';
import Cookies from 'js-cookie';
import { ACCESS_TOKEN_KEY } from '../../../../../../../../utils/constants';

const ITEM_HEIGHT = 48;
const ITEM_PADDING_TOP = 8;
const MenuProps = {
  PaperProps: {
    style: {
      maxHeight: ITEM_HEIGHT * 4.5 + ITEM_PADDING_TOP,
      width: 250,
    },
  },
};

function getStyles(name: string, personName: readonly string[], theme: Theme) {
  return {
    fontWeight:
      personName.indexOf(name) === -1 ? theme.typography.fontWeightRegular : theme.typography.fontWeightMedium,
  };
}

export default function MultipleSelectChip() {
  const theme = useTheme();
  const [personName, setPersonName] = React.useState<string[]>([]);
  const [benefits, setBenefit] = React.useState([]);
  const handleChange = (event: SelectChangeEvent<typeof personName>) => {
    const {
      target: { value },
    } = event;
    setPersonName(typeof value === 'string' ? value.split(',') : value);
  };

  const getBenefit = React.useCallback(async () => {
    const json = await axios.get(API_PATHS.getBenefit, {
      headers: {
        Authorization: 'Bearer ' + Cookies.get(ACCESS_TOKEN_KEY),
      },
    });

    if (json.status === RESPONSE_STATUS_SUCCESS) {
      setBenefit(json.data.data);
    }
  }, []);

  React.useEffect(() => {
    getBenefit();
  }, [getBenefit]);

  console.log(benefits);

  return (
    <Box>
      <FormControl fullWidth>
        <Select
          multiple
          value={personName}
          onChange={handleChange}
          sx={{ background: '#f1f3f5', div: { padding: '5px' }, minHeight: '47px' }}
          renderValue={(selected) => (
            <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 0.5 }}>
              {selected.map((value) => (
                <Chip key={value} label={value} />
              ))}
            </Box>
          )}
          MenuProps={MenuProps}
        >
          {benefits &&
            benefits.map((benefit: any) => (
              <MenuItem key={benefit.id} value={benefit} style={getStyles(benefit.name, personName, theme)}>
                {benefit.name}
              </MenuItem>
            ))}
        </Select>
      </FormControl>
    </Box>
  );
}
