import { Box, Button, FormControl, MenuItem, Select } from '@mui/material';
import { memo, useEffect, useState } from 'react';
import DatePicker from 'react-datepicker';
import KeyboardArrowDownIcon from '@mui/icons-material/KeyboardArrowDown';

import 'react-datepicker/dist/react-datepicker.css';

interface Props {
  open?: boolean;
}

function range(start: number, end: number, step: number = 1): number[] {
  const len = Math.floor((end - start) / step) + 1;
  return Array(len)
    .fill(0)
    .map((_, idx) => start + idx * step);
}

const InputDateTime = ({ open }: Props) => {
  const [startDate, setStartDate] = useState(new Date());
  const [openBar, setOpenBar] = useState<boolean>(true);
  const years = range(1923, new Date().getFullYear() + 1, 1);

  useEffect(() => {
    open && setOpenBar(open);
  }, [open]);
  return (
    <DatePicker
      renderCustomHeader={({ date, changeYear, decreaseMonth, increaseMonth, monthDate }) => (
        <div style={{ margin: 10, display: 'flex', justifyContent: 'center' }}>
          <button
            aria-label="Previous Month"
            className={'react-datepicker__navigation react-datepicker__navigation--previous'}
            onClick={decreaseMonth}
          >
            <span className={'react-datepicker__navigation-icon react-datepicker__navigation-icon--previous'}>
              {'<'}
            </span>
          </button>
          <Box sx={{ position: 'relative' }}>
            <span className="react-datepicker__current-month">
              {monthDate.toLocaleString('en-US', {
                month: 'long',
                year: 'numeric',
              })}
            </span>
            {/* <Box sx={{ minWidth: 120 }}>
              <FormControl fullWidth>
                <Select
                  defaultValue=""
                  onChange={({ target: { value } }) => changeYear(Number(value))}
                  inputProps={{ 'aria-label': 'Without label' }}
                  style={{ background: '#f1f3f5' }}
                  displayEmpty
                >
                  <MenuItem value="" id="menu-item-1" disabled>
                    <em
                      style={{
                        fontSize: '16px',
                        fontFamily: 'SVN-Sofia-Regular',
                        fontStyle: 'normal',
                        color: 'rgb(104, 112, 118)',
                      }}
                    >
                      Choose Gender
                    </em>
                  </MenuItem>
                  {years.map((option) => (
                    <MenuItem key={option} value={option}>
                      {option}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
            </Box> */}
            {/* <select value={date.getFullYear()} onChange={({ target: { value } }) => changeYear(Number(value))}>
              {years.map((option) => (
                <option key={option} value={option}>
                  {option}
                </option>
              ))}
            </select> */}
            <Button>
              <KeyboardArrowDownIcon
                onClick={(e) => {
                  e.stopPropagation();
                  setOpenBar(!openBar);
                }}
              />
            </Button>
            <Box
              sx={{ minHeight: '180px', maxHeight: '180px', overflow: 'auto', minWidth: '125px', position: 'absolute' }}
              hidden={openBar}
            >
              {years.map((option) => (
                <MenuItem
                  key={option}
                  value={option}
                  onClick={(e) => {
                    changeYear(Number(e.currentTarget.value));
                    e.stopPropagation();
                  }}
                >
                  {option}
                </MenuItem>
              ))}
            </Box>
          </Box>

          <button
            aria-label="Next Month"
            className={'react-datepicker__navigation react-datepicker__navigation--next'}
            onClick={increaseMonth}
          >
            <span className={'react-datepicker__navigation-icon react-datepicker__navigation-icon--next'}>{'>'}</span>
          </button>
        </div>
      )}
      selected={startDate}
      onChange={(date: any) => setStartDate(date)}
    />
  );
};

export default memo(InputDateTime);
