import { Box, Grid, InputBase, Stack, Typography } from '@mui/material';
import { FormEvent, memo, useEffect, useState } from 'react';
import { EmployeeParams, EmployeeValidate } from '../../../../../../../../models/employee';
import { validAddEmployee, validateAddEmployee } from '../../../../../../utils';
import InfoForm1 from './infoForm/InfoForm1';
import InfoForm2 from './infoForm/InfoForm2';
import Selected from '../../../selected/Selected';
import { fields1, fields2 } from './fileds';
import { FormattedMessage } from 'react-intl';
import InputDateTime from '../InputDateTime';

function PersonalInformationForm() {
  const [isInputFocused, setInputFocused] = useState(false);
  const [validate, setValidate] = useState<EmployeeValidate>();
  const [validateForm, setValidateForm] = useState<any>({
    gender: '',
    ktp: '',
    name: '',
    ncId: '',
  });
  const hanldeSunmit = (e: FormEvent<HTMLFormElement>) => {
    e.preventDefault();
  };

  const handleInputFocus = () => {
    setInputFocused(true);
  };

  const handleInputBlur = () => {
    setInputFocused(false);
  };

  useEffect(() => {
    if (!isInputFocused && Object.values(validateForm).every((value) => value.trim() === '')) {
      const validate = validateAddEmployee(validateForm);
      console.log(validate);
      setValidate(validate);

      if (!validAddEmployee(validate)) {
        return;
      }
    }
  }, [isInputFocused, validateForm]);

  return (
    <Box
      component="form"
      onSubmit={hanldeSunmit}
      sx={{ gap: '40px', display: 'flex', flexWrap: 'wrap', marginBottom: '20px' }}
    >
      <Stack
        direction="column"
        spacing={2}
        sx={{ flex: '1 1 0%', paddingLeft: '20px', paddingRight: '20px', gap: '10px' }}
      >
        {fields1.map((item: any, index: number) => (
          <Grid
            container
            spacing={2}
            key={index}
            sx={{
              '&:first-of-type': { marginLeft: '0', marginTop: 0 },
              marginTop: '0 !important',
            }}
          >
            <Grid
              item
              xs={12}
              md={5}
              xl={4}
              sx={{
                paddingLeft: '0 !important',
                paddingTop: '0 !important',
                display: 'flex',
                alignItems: 'center',
                marginTop: '0 !important',
              }}
            >
              <Typography
                variant="body2"
                gutterBottom
                sx={{
                  fontFamily: 'SVN-Sofia-Regular',
                  display: 'flex',
                  alignItems: 'center',
                  margin: 0,
                  fontSize: '16px',
                }}
              >
                <FormattedMessage id={item.field} />
                <Box component="span" sx={{ color: 'rgb(229, 72, 77)', margin: 0 }}>
                  {item.required}
                </Box>
              </Typography>
            </Grid>
            <Grid item xs={12} md={7} xl={8} sx={{ paddingLeft: '0 !important', paddingTop: '0 !important' }}>
              {item.field === 'dob' && <InputDateTime />}
              {item.field === 'name' && (
                <div>
                  <InputBase
                    sx={{
                      backgroundColor: !!validate?.name ? 'rgb(255, 239, 239)' : 'rgb(241, 243, 245)',
                      input: { padding: '12px' },
                      borderRadius: '6px',
                      width: '100%',
                      border: !!validate?.name ? '1px solid rgb(243, 174, 175)' : 'none',
                    }}
                    value={item.field === 'name' && validateForm.name}
                    onChange={(e) => setValidateForm({ ...validateForm, name: e.target.value })}
                    type={item.type ? 'text' : 'number'}
                    onFocus={handleInputFocus}
                    onBlur={handleInputBlur}
                  />
                  {!!validate?.name && (
                    <small className="text-danger">
                      <FormattedMessage id={validate?.name} />
                    </small>
                  )}
                </div>
              )}
              {item.field === 'ktp' && (
                <div>
                  <InputBase
                    sx={{
                      backgroundColor: !!validate?.ktp ? 'rgb(255, 239, 239)' : 'rgb(241, 243, 245)',
                      border: !!validate?.ktp ? '1px solid rgb(243, 174, 175)' : 'none',
                      input: { padding: '12px' },
                      borderRadius: '6px',
                      width: '100%',
                    }}
                    value={item.field === 'ktp' && validateForm.ktp}
                    onChange={(e) => setValidateForm({ ...validateForm, ktp: e.target.value })}
                    type={item.type ? 'text' : 'number'}
                    onFocus={handleInputFocus}
                    onBlur={handleInputBlur}
                  />
                  {!!validate?.ktp && (
                    <small className="text-danger">
                      <FormattedMessage id={validate?.ktp} />
                    </small>
                  )}
                </div>
              )}
              {item.field === 'ncId' && (
                <div>
                  <InputBase
                    sx={{
                      backgroundColor: !!validate?.ncId ? 'rgb(255, 239, 239)' : 'rgb(241, 243, 245)',
                      input: { padding: '12px' },
                      borderRadius: '6px',
                      width: '100%',
                      border: !!validate?.ncId ? '1px solid rgb(243, 174, 175)' : 'none',
                    }}
                    value={item.field === 'ncId' && validateForm.ncId}
                    onChange={(e) => setValidateForm({ ...validateForm, ncId: e.target.value })}
                    type={item.type ? 'text' : 'number'}
                    onFocus={handleInputFocus}
                    onBlur={handleInputBlur}
                  />
                  {!!validate?.ncId && (
                    <small className="text-danger">
                      <FormattedMessage id={validate?.ncId} />
                    </small>
                  )}
                </div>
              )}
              {item.input === 'input' && (
                <div>
                  <InputBase
                    sx={{
                      backgroundColor: 'rgb(241, 243, 245)',
                      input: { padding: '12px' },
                      borderRadius: '6px',
                      width: '100%',
                    }}
                    type={item.type ? 'text' : 'number'}
                  />
                </div>
              )}
              {item.input === 'selected' && (
                <div>
                  <Selected defaultOption="Choose Gender" objectOption={['Male', 'Female']} />
                  {!!validate?.gender && (
                    <small className="text-danger">
                      <FormattedMessage id={validate?.gender} />
                    </small>
                  )}
                </div>
              )}
            </Grid>
          </Grid>
        ))}
      </Stack>
      <Stack
        direction="column"
        spacing={2}
        sx={{ flex: '1 1 0%', paddingLeft: '20px', paddingRight: '20px', gap: '10px' }}
      >
        {fields2.map((item: any, index: number) => (
          <Grid
            container
            spacing={2}
            key={index}
            sx={{
              '&:first-of-type': { marginLeft: '0', marginTop: 0 },
              marginTop: '0 !important',
              marginLeft: '-16px !important',
            }}
          >
            <Grid
              item
              xs={12}
              md={5}
              xl={4}
              sx={{
                paddingLeft: '0 !important',
                paddingTop: '0 !important',
                display: 'flex',
                alignItems: 'center',
                marginTop: '0 !important',
              }}
            >
              <Typography
                variant="body2"
                gutterBottom
                sx={{
                  fontFamily: 'SVN-Sofia-Regular',
                  display: 'flex',
                  alignItems: 'center',
                  margin: 0,
                  fontSize: '16px',
                }}
              >
                <FormattedMessage id={item.field} />
                <Box component="span" sx={{ color: 'rgb(229, 72, 77)', margin: 0 }}>
                  {item.required}
                </Box>
              </Typography>
            </Grid>
            <Grid item xs={12} md={7} xl={8} sx={{ paddingLeft: '0 !important', paddingTop: '0 !important' }}>
              {item.input === 'input' && (
                <InputBase
                  sx={{
                    backgroundColor: 'rgb(241, 243, 245)',
                    input: { padding: '12px' },
                    borderRadius: '6px',
                    width: '100%',
                  }}
                  type={item.type ? 'text' : 'number'}
                />
              )}
              {item.input === 'selected' && (
                <Selected
                  defaultOption="Choose Marriage Status"
                  objectOption={['N/A', 'Married with 1 kid', 'Single', 'Married']}
                />
              )}
            </Grid>
          </Grid>
        ))}
      </Stack>
    </Box>
  );
}

export default memo(PersonalInformationForm);
