import { useEffect, useState } from 'react';
import { validAddEmployee, validateAddEmployee } from '../../../../../../../utils';
import { Stack } from '@mui/material';
import ContainerField from './ContainerField';
import FieldInput from './FieldInput';

function Abc() {
  const [validate, setValidate] = useState<any>();

  const [validateForm, setValidateForm] = useState<any>({
    name: '',
    gender: '',
    motherName: '',
    dob: '',
    pob: '',
    ktp: '',
    ncId: '',
    address1: '',
    address2: '',
    mobileNo: '',
    telNo: '',
    bankCard: '',
    bankAccount: '',
    bankName: '',
    familyCardNumber: '',
    safetyInsuranceNo: '',
    healthInsuranceNo: '',
  });

  const handleBlur = () => {
    validateAndHandle(validateForm);
  };

  useEffect(() => {
    if (validateForm.gender !== '' || validateForm.ktp !== '' || validateForm.name !== '' || validateForm.ncId !== '') {
      validateAndHandle(validateForm);
    }
  }, [validateForm]);

  const validateAndHandle = (form: any) => {
    const validate = validateAddEmployee(form);
    setValidate(validate);
    if (!validAddEmployee(validate)) {
      return;
    }
  };

  return (
    <Stack
      direction="column"
      spacing={2}
      sx={{ flex: '1 1 0%', paddingLeft: '20px', paddingRight: '20px', gap: '10px' }}
    >
      <ContainerField nameField="Name" required="*">
        <FieldInput
          validate={validate.name}
          setValidateForm={setValidateForm}
          value={validateForm}
          handleBlur={handleBlur}
        />
      </ContainerField>
    </Stack>
  );
}

export default Abc;
