import { useDispatch } from 'react-redux';
// import './App.css';
import { useSelector } from 'react-redux';
import { Dispatch, useCallback, useEffect, useState } from 'react';
import Header from '../ListItem/Header';
import ContainerSelector from '../ListItem/ContainerSelector';
// import Tables from '../ListItem/Table';
import { ThunkDispatch } from 'redux-thunk';
import { AppState } from '../../../redux/reducer';
import { Action } from 'typesafe-actions';
import { fetchThunk } from '../../common/redux/thunk';
import { API_PATHS } from '../../../configs/api';
import { RESPONSE_STATUS_SUCCESS } from '../../../utils/httpResponseCode';
import Tables from '../ListItem/Table';

function ListItemProduct() {
  const [data, setData] = useState([]);
  const dispatch = useDispatch<ThunkDispatch<AppState, null, Action<string>>>();
  const getLocation = useCallback(async () => {
    const json = await dispatch(fetchThunk(API_PATHS.getProduct, 'get'));

    if (json?.code === RESPONSE_STATUS_SUCCESS) {
      setData(json.data);
      return;
    }
  }, []);
  useEffect(() => {
    getLocation();
  }, []);
  // const handlePrevious = () => {
  //   if (page > 0) {
  //     setPage(page - 1);
  //   }
  // };

  // const handleNext = () => {
  //   if (page < 3) {
  //     setPage(page + 1);
  //   }
  // };

  // const handlePageChange = (pageNum: number) => {
  //   setPage(pageNum);
  // };

  // const prevBtn = page === 1 ? "page-item disabled" : "page-item";
  // const nextBtn = page === 3 ? "page-item disabled" : "page-item";

  return (
    <div className="container mt-3">
      <Header />
      <ContainerSelector />
      <Tables data={data} />
      <nav aria-label="Page navigation example">
        <ul className="pagination justify-content-end">
          <li id="previous" className="page-item">
            <a className="page-link">Previous</a>
          </li>
          {[1, 2, 3].map((pageNum) => (
            <li key={pageNum} className="page-item">
              <a className="page-link">{pageNum}</a>
            </li>
          ))}

          <li id="next" className="page-item">
            <a className="page-link">Next</a>
          </li>
        </ul>
      </nav>
    </div>
  );
}

export default ListItemProduct;
