import axios from 'axios';
import { useEffect, useState } from 'react';
import { Controller, SubmitHandler, useForm } from 'react-hook-form';
import { ILocationParams, ISignUpParams } from '../../../models/singup';

interface Props {
  onSignUp(values: ISignUpParams): void;
  loading: boolean;
  errorMessage: string;
  location: Array<ILocationParams>;
  onChangeRegion(idRegion: string): void;
}

function SignUpForm(props: Props) {
  const [passwordMatch, setPasswordMatch] = useState(true);
  const { onSignUp, loading, errorMessage, location, onChangeRegion } = props;

  const {
    control,
    handleSubmit,
    formState: { errors },
    watch,
  } = useForm<ISignUpParams>({
    defaultValues: {
      email: '',
      password: '',
      repeatPassword: '',
      name: '',
      gender: '',
      region: '',
      state: '',
    },
  });

  useEffect(() => {
    const password = watch('password');
    const repeatPassword = watch('repeatPassword');

    if (password && repeatPassword) {
      setPasswordMatch(password === repeatPassword);
    }
  }, [watch]);

  const onSubmit: SubmitHandler<ISignUpParams> = (data) => onSignUp(data);

  return (
    <form onSubmit={handleSubmit(onSubmit)} style={{ display: 'flex', flexDirection: 'column' }}>
      <div>
        <span>Email</span>
        <Controller
          name="email"
          control={control}
          rules={{
            required: 'Vui lòng nhập email',
            pattern: {
              value: /^[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,4}$/,
              message: 'Định dạng email không đúng',
            },
          }}
          render={({ field }) => <input {...field} type="text" id="email" />}
        />
        {errors.email && <span className="text-danger">{errors.email.message}</span>}
      </div>
      <div>
        <span>Mật khẩu</span>
        <Controller
          name="password"
          control={control}
          rules={{
            required: 'Vui lòng nhập mật khẩu',
            minLength: {
              value: 4,
              message: 'Mật khẩu tối thiểu 4 ký tự',
            },
          }}
          render={({ field }) => <input {...field} type="password" id="password" />}
        />
        {errors.password && <span className="text-danger">{errors.password.message}</span>}
      </div>
      <div>
        <span>Xác nhận mật khẩu</span>
        <Controller
          name="password"
          control={control}
          rules={{
            required: 'Vui lòng nhập lại mật khẩu',
            validate: (value) => value === watch('password') || 'Mật khẩu xác nhận không chính xác',
          }}
          render={({ field }) => <input {...field} type="password" id="password" />}
        />
        {!passwordMatch && <span className="text-danger">Mật khẩu xác nhận không chính xác</span>}
      </div>
    </form>
  );
}

export default SignUpForm;
