import { useDispatch } from 'react-redux';
import { ThunkDispatch } from 'redux-thunk';
import { AppState } from '../../../redux/reducer';
import { Action } from 'typesafe-actions';
import { ChangeEvent, useCallback, useEffect, useRef, useState } from 'react';
import { fetchThunk } from '../../common/redux/thunk';
import { API_PATHS } from '../../../configs/api';
import { RESPONSE_STATUS_SUCCESS } from '../../../utils/httpResponseCode';
import { IUser } from '../../../models/user';
import { APIUrl } from '../../../utils/constants';
import './UserPage.scss';
import { logout } from '../redux/authReducer';
import { ROUTES } from '../../../configs/routes';
import { replace } from 'connected-react-router';

function UserPage() {
  const dispatch = useDispatch<ThunkDispatch<AppState, null, Action<string>>>();
  const [loading, setLoading] = useState(false);
  const avatarInputRef = useRef<HTMLInputElement>(null);
  const [data, setData] = useState<IUser>();
  const [image, setImage] = useState(data?.avatar);

  const getUser = useCallback(async () => {
    setLoading(true);
    const json = await dispatch(fetchThunk(API_PATHS.userProfile, 'get'));
    setLoading(false);

    if (json?.code === RESPONSE_STATUS_SUCCESS) {
      setData(json.data);
    }
  }, [dispatch]);

  useEffect(() => {
    getUser();
  }, [getUser]);

  const onLogOut = () => {
    dispatch(logout());
    dispatch(replace(ROUTES.loginV2));
  };

  const changeAvatar = () => {
    if (avatarInputRef.current !== null) avatarInputRef.current.click();
  };

  const onChooseAvatar = (e: ChangeEvent<HTMLInputElement>) => {
    e.preventDefault();
    const file = e.target.files;
    const reader = new FileReader();
    reader.onload = () => {
      set;
    };
  };

  if (loading) {
    return <h3>Loading....</h3>;
  }
  return (
    <div className="container">
      <div className="card" style={{ margin: 'auto', width: '100%', alignItems: 'center' }}>
        <div className="profilepic">
          <img src={`${APIUrl}/${data?.avatar}`} className="card-img-top profilepic__image" alt="avatar_url" />
          <div className="profilepic" onClick={changeAvatar}>
            <input type="file" ref={avatarInputRef} hidden accept="image/*" onChange={onChooseAvatar} />
            <span className="profilepic__text">Upload</span>
          </div>
        </div>
        <div className="card-body">
          <h5 className="card-title">Email</h5>
          <p className="card-text">{data?.email}</p>
          <h5 className="card-title">data Name</h5>
          <p className="card-text">{data?.name}</p>
          <h5 className="card-title">Description</h5>
          <p className="card-text">{data?.description}</p>
          <h5 className="card-title">State</h5>
          <p className="card-text">{data?.state}</p>
          <h5 className="card-title">Region</h5>
          <p className="card-text">{data?.region}</p>
          <button className="btn btn-primary" onClick={onLogOut}>
            Log Out
          </button>
        </div>
      </div>
    </div>
  );
}

export default UserPage;
