import { useForm, SubmitHandler } from 'react-hook-form';
import { ILoginParams } from '../../../models/auth';

interface Props {
  onLogin(values: ILoginParams): void;
  loading: boolean;
}

export default function LoginFormV2(props: Props) {
  const { onLogin, loading } = props;
  const {
    register,
    formState: { errors },
    handleSubmit,
  } = useForm<ILoginParams>();
  const onSubmit: SubmitHandler<ILoginParams> = (data) => onLogin(data);

  return (
    <form onSubmit={handleSubmit(onSubmit)}>
      <input {...register('email', { required: true, pattern: /^[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,4}$/ })} />
      {errors.email?.type === 'required' && 'Vui lòng nhập địa chỉ email'}
      {errors.email?.type === 'pattern' && 'Địa chỉ email không hợp lệ'}
      <input {...register('password', { minLength: 4, required: true })} />
      {errors.password?.type === 'required' && 'Vui lòng nhập mật khẩu'}
      {errors.password?.type === 'minLength' && 'Mật khẩu tối thiểu 4 ký tự'}
      <input type="checkbox" />
      <div className="row justify-content-md-center" style={{ margin: '16px 0' }}>
        <div className="col-md-auto">
          <button
            className="btn btn-primary"
            type="submit"
            style={{ minWidth: '160px', display: 'flex', alignItems: 'center', justifyContent: 'center' }}
            disabled={loading}
          >
            {loading && <div className="spinner-border spinner-border-sm text-light mr-2" role="status" />}
          </button>
        </div>
      </div>
    </form>
  );
}
