import { Box, FormControl, MenuItem, Select, SelectChangeEvent } from '@mui/material';
import '../../../LoginPage.scss';

const styled = { fontFamily: 'SVN-Sofia-Regular' };
interface Props {
  defaultOption: string;
  objectOption: string[];
  setValue?: any;
  value?: any;
  styleSelect?: any;
}

function Selected({ defaultOption, objectOption, setValue, value, styleSelect }: Props) {
  const handleChange = (e: SelectChangeEvent) => {
    setValue({ ...value, gender: e.target.value });
    console.log(value);
  };
  return (
    <Box sx={{ minWidth: 120 }}>
      <FormControl fullWidth>
        <Select
          defaultValue=""
          onChange={handleChange}
          inputProps={{ 'aria-label': 'Without label' }}
          sx={{ background: '#f1f3f5', div: { padding: '12px' } }}
          style={styleSelect}
          displayEmpty
          className="ip"
        >
          <MenuItem value="" id="menu-item-1">
            <em
              style={{
                fontSize: '16px',
                fontFamily: 'SVN-Sofia-Regular',
                fontStyle: 'normal',
                color: 'rgb(104, 112, 118)',
              }}
            >
              {defaultOption}
            </em>
          </MenuItem>
          {objectOption.map((option: string, index: number) => (
            <MenuItem key={index} style={styled} className="menu-item" value={index}>
              {option}
            </MenuItem>
          ))}
        </Select>
      </FormControl>
    </Box>
  );
}

export default Selected;
