import { useForm, SubmitHandler } from 'react-hook-form';
import { ILoginParams } from '../../../models/auth';

const onSubmit: SubmitHandler<ILoginParams> = (data) => console.log(data);
export default function LoginFormV2() {
  const {
    register,
    formState: { errors },
    handleSubmit,
  } = useForm<ILoginParams>();

  return (
    <form onSubmit={handleSubmit(onSubmit)}>
      <input {...register('email', { required: true, pattern: /^[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,4}$/ })} />
      {errors.email?.type === 'required' && 'Vui lòng nhập địa chỉ email'}
      {errors.email?.type === 'pattern' && 'Địa chỉ email không hợp lệ'}
      <input {...register('password', { minLength: 4, required: true })} />
      {errors.password?.type === 'required' && 'Vui lòng nhập mật khẩu'}
      {errors.password?.type === 'minLength' && 'Mật khẩu tối thiểu 4 ký tự'}
      <input type="checkbox" />
      <input type="submit" />
    </form>
  );
}
