import { Box, FormControl, Grid, InputBase, MenuItem, Select, Stack, Typography } from '@mui/material';
import { fields1 } from '../fileds';
import { FormattedMessage } from 'react-intl';
import InputDateTime from '../../InputDateTime';

import { FocusEventHandler, useEffect, useState } from 'react';
import { validAddEmployee, validFieldName, validateAddEmployee, validateFieldName } from '../../../../../../../utils';
import { useDispatch } from 'react-redux';
import { ThunkDispatch } from 'redux-thunk';
import { AppState } from '../../../../../../../../../redux/reducer';
import { Action } from 'typesafe-actions';
import { setEmployeeAdd } from '../../../../../../../redux/employeeReducer';

const objectOption = ['Male', 'Female'];

function InfoForm1() {
  const [validate, setValidate] = useState<any>();
  const dispatch = useDispatch<ThunkDispatch<AppState, null, Action<string>>>();

  const [validateForm, setValidateForm] = useState<any>({
    name: '',
    gender: '',
    motherName: '',
    dob: '',
    pob: '',
    ktp: '',
    ncId: '',
    address1: '',
    address2: '',
    mobileNo: '',
    telNo: '',
    bankCard: '',
    bankAccount: '',
    bankName: '',
    familyCardNumber: '',
    safetyInsuranceNo: '',
    healthInsuranceNo: '',
  });

  const handleBlur = () => {
    const validate = validateFieldName(validateForm);

    setValidate(validate);
    if (!validFieldName(validate)) {
      return;
    }
    // let validate = '';
    // const field = e.target.name;
    // if (field === 'name') {
    //   validate = validateAddEmployee(e.target.value);

    //   setValidate(validate);
    //   if (!validName(validate)) {
    //     return;
    //   }
    // }
  };

  useEffect(() => {
    if (validateForm.gender !== '' || validateForm.ktp !== '' || validateForm.name !== '' || validateForm.ncId !== '') {
      const validate = validateAddEmployee(validateForm);

      setValidate(validate);
      dispatch(setEmployeeAdd(validateForm));
      if (!validAddEmployee(validate)) {
        return;
      }
    }
  }, [validateForm, dispatch]);

  return (
    <Stack
      direction="column"
      spacing={2}
      sx={{ flex: '1 1 0%', paddingLeft: '20px', paddingRight: '20px', gap: '10px' }}
    >
      {fields1.map((item: any, index: number) => (
        <Grid
          container
          spacing={2}
          key={index}
          sx={{
            '&:first-of-type': { marginLeft: '0', marginTop: 0 },
            marginTop: '0 !important',
          }}
        >
          <Grid
            item
            xs={12}
            md={5}
            xl={4}
            sx={{
              paddingLeft: '0 !important',
              paddingTop: '0 !important',
              display: 'flex',
              alignItems: 'center',
              marginTop: '0 !important',
            }}
          >
            <Typography
              variant="body2"
              gutterBottom
              sx={{
                fontFamily: 'SVN-Sofia-Regular',
                display: 'flex',
                alignItems: 'center',
                margin: 0,
                fontSize: '16px',
              }}
            >
              <FormattedMessage id={item.field} />
              <Box component="span" sx={{ color: 'rgb(229, 72, 77)', margin: 0 }}>
                {item.required}
              </Box>
            </Typography>
          </Grid>
          <Grid item xs={12} md={7} xl={8} sx={{ paddingLeft: '0 !important', paddingTop: '0 !important' }}>
            {item.field === 'dob' && <InputDateTime />}
            {item.field === 'name' && (
              <div>
                <InputBase
                  sx={{
                    backgroundColor: !!validate?.name ? 'rgb(255, 239, 239)' : 'rgb(241, 243, 245)',
                    input: { padding: '12px' },
                    borderRadius: '6px',
                    width: '100%',
                    border: !!validate?.name ? '1px solid rgb(243, 174, 175)' : 'none',
                  }}
                  value={item.field === 'name' && validateForm.name}
                  name={item.field}
                  onChange={(e) => {
                    setValidateForm({ ...validateForm, name: e.target.value });
                    // handleChangeForm();
                  }}
                  type={item.type ? 'text' : 'number'}
                  onBlur={handleBlur}
                />
                {!!validate?.name && (
                  <small className="text-danger">
                    <FormattedMessage id={validate?.name} />
                  </small>
                )}
              </div>
            )}
            {item.field === 'ktp' && (
              <div>
                <InputBase
                  sx={{
                    backgroundColor: !!validate?.ktp ? 'rgb(255, 239, 239)' : 'rgb(241, 243, 245)',
                    border: !!validate?.ktp ? '1px solid rgb(243, 174, 175)' : 'none',
                    input: { padding: '12px' },
                    borderRadius: '6px',
                    width: '100%',
                  }}
                  value={item.field === 'ktp' && validateForm.ktp}
                  onChange={(e) => {
                    setValidateForm({ ...validateForm, ktp: e.target.value });
                    // handleChangeForm();
                  }}
                  type={item.type ? 'text' : 'number'}
                  onBlur={handleBlur}
                />
                {!!validate?.ktp && (
                  <small className="text-danger">
                    <FormattedMessage id={validate?.ktp} />
                  </small>
                )}
              </div>
            )}
            {item.field === 'ncId' && (
              <div>
                <InputBase
                  sx={{
                    backgroundColor: !!validate?.ncId ? 'rgb(255, 239, 239)' : 'rgb(241, 243, 245)',
                    border: !!validate?.ncId ? '1px solid rgb(243, 174, 175)' : 'none',
                    input: { padding: '12px' },
                    borderRadius: '6px',
                    width: '100%',
                  }}
                  value={item.field === 'ncId' && validateForm.ncId}
                  onChange={(e) => {
                    setValidateForm({ ...validateForm, ncId: e.target.value });
                    // handleChangeForm();
                  }}
                  type={item.type ? 'text' : 'number'}
                  onBlur={handleBlur}
                />
                {!!validate?.ncId && (
                  <small className="text-danger">
                    <FormattedMessage id={validate?.ncId} />
                  </small>
                )}
              </div>
            )}
            {item.input === 'input' && (
              <div>
                <InputBase
                  sx={{
                    backgroundColor: 'rgb(241, 243, 245)',
                    input: { padding: '12px' },
                    borderRadius: '6px',
                    width: '100%',
                  }}
                  type={item.type ? 'text' : 'number'}
                />
              </div>
            )}
            {item.input === 'selected' && (
              <div>
                <Box sx={{ minWidth: 120 }}>
                  <FormControl fullWidth>
                    <Select
                      value={validateForm.gender}
                      onChange={(e) => {
                        setValidateForm({ ...validateForm, gender: e.target.value });
                        const validate = validateAddEmployee(validateForm);

                        setValidate(validate);
                        if (!validAddEmployee(validate)) {
                          return;
                        }
                      }}
                      inputProps={{ 'aria-label': 'Without label' }}
                      sx={{
                        backgroundColor: !!validate?.gender ? 'rgb(255, 239, 239)' : 'rgb(241, 243, 245)',
                        border: !!validate?.gender ? '1px solid rgb(243, 174, 175)' : 'none',
                        div: { padding: '12px' },
                      }}
                      displayEmpty
                      className="ip"
                      onBlur={handleBlur}
                    >
                      <MenuItem value="" id="menu-item-1">
                        <em
                          style={{
                            fontSize: '16px',
                            fontFamily: 'SVN-Sofia-Regular',
                            fontStyle: 'normal',
                            color: 'rgb(104, 112, 118)',
                          }}
                        >
                          Choose Gender
                        </em>
                      </MenuItem>
                      {objectOption.map((option: string, index: number) => (
                        <MenuItem key={index} className="menu-item" value={option}>
                          {option}
                        </MenuItem>
                      ))}
                    </Select>
                  </FormControl>
                </Box>
                {!!validate?.gender && (
                  <small className="text-danger">
                    <FormattedMessage id={validate?.gender} />
                  </small>
                )}
              </div>
            )}
          </Grid>
        </Grid>
      ))}
    </Stack>
  );
}

export default InfoForm1;
