import { useEffect, useState } from 'react';
import { Controller, SubmitHandler, useForm } from 'react-hook-form';
import { ILocationParams, ISignUpParams } from '../../../models/singup';
import configureStore from '../../../redux/configureStore';
import { setLocale } from '../../intl/redux/intlReducer';
import { FormattedMessage } from 'react-intl';
import { ILoginValidation } from '../../../models/auth';
import { validSignUp, validateSignUp } from '../utils';

interface Props {
  onSignUp(values: ISignUpParams): void;
  loading: boolean;
  errorMessage: string;
  locations: Array<ILocationParams>;
  states: Array<ILocationParams>;
  onChangeRegion(idRegion: string): void;
}

function SignUpForm(props: Props) {
  const [passwordMatch, setPasswordMatch] = useState(true);
  const { onSignUp, loading, errorMessage, locations, states, onChangeRegion } = props;
  const [showCityField, setShowCityField] = useState(false);
  const [validate, setValidate] = useState<ILoginValidation>();

  const {
    register,
    handleSubmit,
    formState: { errors },
    watch,
  } = useForm<ISignUpParams>({
    defaultValues: {
      email: '',
      password: '',
      repeatPassword: '',
      name: '',
      gender: '',
      region: '',
      state: '',
    },
  });

  const options = [
    {
      label: 'Nam',
      value: 'male',
    },
    {
      label: 'Nữ',
      value: 'female',
    },
    {
      label: 'Bede',
      value: 'bede',
    },
  ];

  useEffect(() => {
    const password = watch('password');
    const repeatPassword = watch('repeatPassword');
    const region = watch('region');
    if (password && repeatPassword) {
      setPasswordMatch(password === repeatPassword);
    }
    onChangeRegion(region);

    if (region) {
      setShowCityField(true);
    }
  }, [watch('password'), watch('repeatPassword'), watch('region')]);

  const onSubmit: SubmitHandler<ISignUpParams> = (data) => {
    onSignUp(data);
    const validate = validateSignUp(data);
    setValidate(validate);

    if (!validSignUp(validate)) {
      return;
    }
  };

  return (
    <form
      onSubmit={handleSubmit(onSubmit)}
      className="row g-3 needs-validation"
      style={{ display: 'flex', flexDirection: 'column' }}
    >
      {!!errorMessage && (
        <div className="alert alert-danger" role="alert" style={{ width: '100%' }}>
          {errorMessage}
        </div>
      )}
      <div className="d-flex flex-column">
        <label htmlFor="inputEmail" className="form-label">
          <FormattedMessage id="email" />
        </label>
        {/* <Controller
          name="email"
          control={control}
          rules={{
            required: validate?.email,
            pattern: {
              value: /^[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,4}$/,
              message: 'Định dạng email không đúng',
            },
          }}
          render={({ field }) => <input {...field} type="text" id="email" />}
        /> */}
        <input type="email" {...register('email')} />
        {!!validate?.email && (
          <small className="text-danger">
            <FormattedMessage id={validate?.email} />
          </small>
        )}
      </div>
      <div>
        <input type="password" {...register('password')} />
        {!!validate?.email && (
          <small className="text-danger">
            <FormattedMessage id={validate?.email} />
          </small>
        )}
      </div>
      {/* <div className="d-flex flex-column">
        <label htmlFor="inputEmail" className="form-label">
          <FormattedMessage id="password" />
        </label>
        <Controller
          name="password"
          control={control}
          rules={{
            required: 'Vui lòng nhập mật khẩu',
            minLength: {
              value: 4,
              message: 'Mật khẩu tối thiểu 4 ký tự',
            },
          }}
          render={({ field }) => <input {...field} type="password" id="password" />}
        />
        {errors.password && <span className="text-danger">{errors.password.message}</span>}
      </div>
      <div className="d-flex flex-column">
        <label htmlFor="inputEmail" className="form-label">
          <FormattedMessage id="repeatPassword" />
        </label>
        <Controller
          name="repeatPassword"
          control={control}
          rules={{
            required: 'Vui lòng nhập lại mật khẩu',
            validate: (value) => value === watch('password') || 'Mật khẩu xác nhận không chính xác',
          }}
          render={({ field }) => <input {...field} type="password" id="repeatPassword" />}
        />
        {!passwordMatch && <span className="text-danger">Mật khẩu xác nhận không chính xác</span>}
      </div>
      <div className="d-flex flex-column">
        <label htmlFor="inputEmail" className="form-label">
          <FormattedMessage id="name" />
        </label>
        <Controller
          name="name"
          control={control}
          rules={{
            required: 'Vui lòng nhập tên',
          }}
          render={({ field }) => <input {...field} type="text " id="name" />}
        />
      </div>
      <div className="d-flex flex-column">
        <label htmlFor="inputEmail" className="form-label">
          <FormattedMessage id="gender" />
        </label>
        <Controller
          name="gender"
          control={control}
          rules={{ required: 'Vui lòng nhập giới tính' }}
          render={({ field: { onChange, value } }) => (
            <select onChange={onChange} value={value}>
              <option>-- Vui lòng chọn giới tính --</option>
              {options.map((option) => (
                <option key={option.value} value={option.value}>
                  {option.label}
                </option>
              ))}
            </select>
          )}
        />
      </div>
      <div className="d-flex flex-column">
        <label htmlFor="inputEmail" className="form-label">
          <FormattedMessage id="region" />
        </label>
        <Controller
          name="region"
          control={control}
          rules={{ required: 'Vui lòng chọn quốc gia' }}
          render={({ field: { onChange, value } }) => (
            <select onChange={onChange} value={value}>
              <option>-- Vui lòng chọn quốc gia --</option>
              {locations.map((location: ILocationParams, index: number) => (
                <option key={location.id} value={index + 1}>
                  {location.name}
                </option>
              ))}
            </select>
          )}
        />
      </div>
      {showCityField && (
        <div className="d-flex flex-column">
          <label htmlFor="inputEmail" className="form-label">
            <FormattedMessage id="state" />
          </label>
          <Controller
            name="state"
            control={control}
            rules={{ required: 'Vui lòng chọn quốc gia' }}
            render={({ field: { onChange, value } }) => (
              <select onChange={onChange} value={value}>
                {states.map((state: ILocationParams, index: number) => (
                  <option key={state.id} value={index}>
                    {state.name}
                  </option>
                ))}
              </select>
            )}
          />
        </div>
      )} */}
      <div className="row justify-content-md-center" style={{ margin: '16px 0' }}>
        <div className="col-md-auto d-flex">
          <button
            className="btn btn-primary me-3"
            type="submit"
            style={{ minWidth: '160px', display: 'flex', alignItems: 'center', justifyContent: 'center' }}
            disabled={loading}
          >
            {loading && <div className="spinner-border spinner-border-sm text-light mr-2" role="status" />} Đăng ký
          </button>
        </div>
      </div>
    </form>
  );
}

export default SignUpForm;
