import * as React from 'react';
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Divider from '@mui/material/Divider';
import Drawer from '@mui/material/Drawer';
import InboxIcon from '@mui/icons-material/MoveToInbox';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import MailIcon from '@mui/icons-material/Mail';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import { Button, FormControl, MenuItem, Select, SelectChangeEvent } from '@mui/material';

import logo from '../../../../imagies/Rectangle4.png';
import logoUsa from '../../../../imagies/usa.png';

import './EmployeeManagementPage.scss';
import { useDispatch } from 'react-redux';
import { ThunkDispatch } from 'redux-thunk';
import { AppState } from '../../../../redux/reducer';
import { Action } from 'typesafe-actions';
import { setLocale } from '../../../intl/redux/intlReducer';
import { FormattedMessage } from 'react-intl';
import Info from './layouts/Info';

const drawerWidth = 240;

interface Props {
  window?: () => Window;
}

export default function EmployeeManagementPage(props: Props) {
  const { window } = props;
  const [mobileOpen, setMobileOpen] = React.useState<boolean>(false);
  const [tran, setTran] = React.useState<string>('en');
  const [open, setOpen] = React.useState<boolean>(false);
  const ref = React.useRef<HTMLDivElement>(null);

  const handleChange = (event: SelectChangeEvent) => {
    setTran(event.target.value);
  };

  const handleOpenInfo = () => {
    setOpen(!open);
  };

  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };

  React.useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (ref.current && !ref.current.contains(event.target as Node)) {
        setOpen(false);
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [ref]);

  const dispatch = useDispatch<ThunkDispatch<AppState, null, Action<string>>>();

  React.useEffect(() => {
    dispatch(setLocale(tran));
  }, [tran, dispatch]);

  const drawer = (
    <div>
      <Toolbar />
      <Divider />
      <List>
        {['Inbox', 'Starred', 'Send email', 'Drafts'].map((text, index) => (
          <ListItem key={text} disablePadding>
            <ListItemButton>
              <ListItemIcon>{index % 2 === 0 ? <InboxIcon /> : <MailIcon />}</ListItemIcon>
              <ListItemText primary={text} />
            </ListItemButton>
          </ListItem>
        ))}
      </List>
      <Divider />
      <List>
        {['All mail', 'Trash', 'Spam'].map((text, index) => (
          <ListItem key={text} disablePadding>
            <ListItemButton>
              <ListItemIcon>{index % 2 === 0 ? <InboxIcon /> : <MailIcon />}</ListItemIcon>
              <ListItemText primary={text} />
            </ListItemButton>
          </ListItem>
        ))}
      </List>
    </div>
  );

  const container = window !== undefined ? () => window().document.body : undefined;

  return (
    <Box sx={{ display: 'flex' }}>
      <AppBar className="header">
        <Toolbar>
          <img src={logo} alt="" className="img" />
          <Typography variant="h6" noWrap component="div" sx={{ color: '#000' }} className="header-title">
            <label htmlFor="inputEmail" className="form-label">
              <FormattedMessage id="title" />
            </label>
          </Typography>
        </Toolbar>
        <Toolbar>
          <Box sx={{ minWidth: 120 }}>
            <FormControl fullWidth>
              <Select
                defaultValue=""
                onChange={handleChange}
                inputProps={{ 'aria-label': 'Without label' }}
                style={{ background: '#f1f3f5' }}
                displayEmpty
                className="ip"
              >
                <MenuItem value="" id="menu-item-1" disabled>
                  <Button variant="text" className="btn-tran">
                    <div className="container-tran">
                      <img src={logoUsa} alt="USA" className="container-tran-img" />
                      <span className="item-tran-span">EN</span>
                    </div>
                  </Button>
                </MenuItem>
                <MenuItem value="en" className="item-tran">
                  <Button variant="text" className="btn-container">
                    <div className="container-item-tran">
                      <img src={logoUsa} alt="USA" className="container-tran-img" />
                      <span className="item-tran-span">EN</span>
                    </div>
                  </Button>
                </MenuItem>
                <MenuItem value="vi" className="item-tran">
                  <Button variant="text" className="btn-container">
                    <div className="container-item-tran">
                      <img src={logoUsa} alt="USA" className="container-tran-img" />
                      <span className="item-tran-span">VI</span>
                    </div>
                  </Button>
                </MenuItem>
              </Select>
            </FormControl>
          </Box>
          <div
            style={{ boxShadow: 'none', cursor: 'pointer' }}
            className="btn btn-info"
            onClick={handleOpenInfo}
            ref={ref}
          >
            <span>t</span>
          </div>
          {open && (
            <div ref={ref}>
              <Info />
            </div>
          )}
        </Toolbar>
      </AppBar>
      <Box
        component="nav"
        sx={{ width: { sm: drawerWidth }, flexShrink: { sm: 0 }, background: '#FBFCFD', zIndex: '1' }}
        aria-label="mailbox folders"
      >
        {/* The implementation can be swapped with js to avoid SEO duplication of links. */}
        <Drawer
          container={container}
          variant="temporary"
          open={mobileOpen}
          onClose={handleDrawerToggle}
          ModalProps={{
            keepMounted: true, // Better open performance on mobile.
          }}
          sx={{
            display: { xs: 'block', sm: 'none' },
            '& .MuiDrawer-paper': { boxSizing: 'border-box', width: drawerWidth },
          }}
        >
          {drawer}
        </Drawer>
        <Drawer
          variant="permanent"
          sx={{
            display: { xs: 'none', sm: 'block' },
            '& .MuiDrawer-paper': { boxSizing: 'border-box', width: drawerWidth },
          }}
          open
        >
          {drawer}
        </Drawer>
      </Box>
      <Box
        component="main"
        sx={{ flexGrow: 1, p: 3, background: '#FBFCFD', width: { sm: `calc(100% - ${drawerWidth}px)` } }}
      >
        <Toolbar />
        <Typography paragraph>
          Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore
          magna aliqua. Rhoncus dolor purus non enim praesent elementum facilisis leo vel. Risus at ultrices mi tempus
