import ArrowBackIosIcon from '@mui/icons-material/ArrowBackIos';
import ArrowForwardIosIcon from '@mui/icons-material/ArrowForwardIos';
import KeyboardDoubleArrowLeftIcon from '@mui/icons-material/KeyboardDoubleArrowLeft';
import KeyboardDoubleArrowRightIcon from '@mui/icons-material/KeyboardDoubleArrowRight';

import './Pagination.scss';

interface Props {
  onChangePage(page: number): void;
  dataAll: any;
}

function Pagination(props: Props) {
  const { onChangePage, dataAll } = props;

  console.log(dataAll);

  return (
    <div className="d-flex">
      <button className="btn " disabled>
        <KeyboardDoubleArrowLeftIcon />
      </button>
      <button className="btn ">
        <ArrowBackIosIcon />
      </button>
      <div>
        {[...Array(dataAll.last_page)].map((page, index) => {
          const page_number = index + 1;
          if (page_number === dataAll.current_page) {
            return (
              <button key={index} className="btn btn-custom active">
                {page_number}
              </button>
            );
          }
          if (
            page_number === dataAll.current_page - 1 ||
            page_number === dataAll.current_page + 1 ||
            page_number === 1 ||
            page_number === dataAll.last_page
          ) {
            return (
              <button key={index} className="btn btn-custom">
                {page_number}
              </button>
            );
          }
          if (page_number === dataAll.current_page - 2 || page_number === dataAll.current_page + 2) {
            return <span key={index}>...</span>;
          }
          return null;
        })}
      </div>
      <button className="btn btn-custom-next">
        <ArrowForwardIosIcon />
      </button>
      <button className="btn btn-custom-next">
        <KeyboardDoubleArrowRightIcon />
      </button>
      <div className="btn-total">
        <p>
          {dataAll.from} - {dataAll.to} of {dataAll.total}
        </p>
      </div>
    </div>
  );
}

export default Pagination;
