import ArrowBackIosIcon from '@mui/icons-material/ArrowBackIos';
import ArrowForwardIosIcon from '@mui/icons-material/ArrowForwardIos';
import KeyboardDoubleArrowLeftIcon from '@mui/icons-material/KeyboardDoubleArrowLeft';
import KeyboardDoubleArrowRightIcon from '@mui/icons-material/KeyboardDoubleArrowRight';

import './Pagination.scss';
import React, { memo } from 'react';

interface Props {
  onChangePage(page: number): void;
  dataAll: any;
}

function Pagination(props: Props) {
  const { onChangePage, dataAll } = props;
  const [page, setPage] = React.useState<number>(1);

  const handleClick = (e: React.MouseEvent<HTMLButtonElement, MouseEvent>) => {
    setPage(Number(e.currentTarget.value));
  };
  React.useEffect(() => {
    page && onChangePage(page);
  }, [page, onChangePage]);

  console.log(page);

  return (
    <div className="d-flex">
      <div className="d-flex">
        <button
          className={`btn ${page === 1 ? '' : 'btn-custom-next'}`}
          style={{ boxShadow: 'none' }}
          disabled={page === 1 ? true : false}
        >
          <KeyboardDoubleArrowLeftIcon />
        </button>
        <button
          className={`btn ${page === 1 ? '' : 'btn-custom-next'}`}
          style={{ boxShadow: 'none' }}
          disabled={page === 1 ? true : false}
        >
          <ArrowBackIosIcon />
        </button>
        <div className="d-flex">
          {Array(dataAll.last_page)
            .fill(null)
            .map((_, index) => {
              const pageNumber = index + 1;
              const isActive = pageNumber === dataAll.current_page;

              return (
                <button
                  key={pageNumber}
                  value={pageNumber}
                  className={`btn btn-custom ${isActive ? 'active' : ''}`}
                  onClick={handleClick}
                  style={{ boxShadow: 'none' }}
                >
                  {pageNumber}
                </button>
              );
            })}
        </div>

        <button className="btn btn-custom-next" style={{ boxShadow: 'none' }}>
          <ArrowForwardIosIcon />
        </button>
        <button className="btn btn-custom-next" style={{ boxShadow: 'none' }}>
          <KeyboardDoubleArrowRightIcon />
        </button>
      </div>
      <div className="btn-total mx-4">
        <p>
          {dataAll.from} - {dataAll.to} of {dataAll.total}
        </p>
      </div>
    </div>
  );
}

export default memo(Pagination);
