import { Box, FormControl, Grid, InputBase, MenuItem, Select, Stack, Typography } from '@mui/material';
import { fields2 } from '../fileds';
import { FormattedMessage } from 'react-intl';
import Selected from '../../../../selected/Selected';
import ContainerField from './ContainerField';
import { useState } from 'react';

function InfoForm2() {
  const [validateForm, setValidateForm] = useState<any>({
    mobileNo: '',
    marriageStatus: '',
    telNo: '',
    bankCard: '',
    bankAccount: '',
    bankName: '',
    familyCardNumber: '',
    safetyInsuranceNo: '',
    healthInsuranceNo: '',
  });
  return (
    <Stack
      direction="column"
      spacing={2}
      sx={{ flex: '1 1 0%', paddingLeft: '20px', paddingRight: '20px', gap: '10px' }}
    >
      <ContainerField nameField="mobileNo">
        <InputBase
          sx={{
            backgroundColor: 'rgb(241, 243, 245)',
            input: { padding: '12px' },
            borderRadius: '6px',
            width: '100%',
          }}
          value={validateForm.mobileNo}
          onChange={(e) => setValidateForm({ ...validateForm, mobileNo: e.target.value })}
        />
      </ContainerField>
      <ContainerField nameField="telNo">
        <InputBase
          sx={{
            backgroundColor: 'rgb(241, 243, 245)',
            input: { padding: '12px' },
            borderRadius: '6px',
            width: '100%',
          }}
          value={validateForm.telNo}
          onChange={(e) => setValidateForm({ ...validateForm, telNo: e.target.value })}
        />
      </ContainerField>
      <ContainerField nameField="marriageStatus">
        <Box sx={{ minWidth: 120 }}>
          <FormControl fullWidth>
            <Select
              value={validateForm.marriageStatus}
              onChange={(e) => {
                setValidateForm({ ...validateForm, marriageStatus: e.target.value });
              }}
              inputProps={{ 'aria-label': 'Without label' }}
              sx={{
                backgroundColor: 'rgb(241, 243, 245)',
                div: { padding: '12px' },
              }}
              displayEmpty
              className="ip"
            >
              <MenuItem value="" id="menu-item-1">
                <em
                  style={{
                    fontSize: '16px',
                    fontFamily: 'SVN-Sofia-Regular',
                    fontStyle: 'normal',
                    color: 'rgb(104, 112, 118)',
                  }}
                >
                  Choose Gender
                </em>
              </MenuItem>
              {['N/A', 'Married with 1 kid', 'Single', 'Married'].map((option: string, index: number) => (
                <MenuItem key={index} className="menu-item" value={option}>
                  {option}
                </MenuItem>
              ))}
            </Select>
          </FormControl>
        </Box>
      </ContainerField>
      <ContainerField nameField="bankCardNo">
        <InputBase
          sx={{
            backgroundColor: 'rgb(241, 243, 245)',
            input: { padding: '12px' },
            borderRadius: '6px',
            width: '100%',
          }}
          value={validateForm.bankCard}
          onChange={(e) => setValidateForm({ ...validateForm, bankCard: e.target.value })}
        />
      </ContainerField>
      <ContainerField nameField="bankAccountNo">
        <InputBase
          sx={{
            backgroundColor: 'rgb(241, 243, 245)',
            input: { padding: '12px' },
            borderRadius: '6px',
            width: '100%',
          }}
          value={validateForm.bankAccount}
          onChange={(e) => setValidateForm({ ...validateForm, bankAccount: e.target.value })}
        />
      </ContainerField>
      <ContainerField nameField="bankName">
        <InputBase
          sx={{
            backgroundColor: 'rgb(241, 243, 245)',
            input: { padding: '12px' },
            borderRadius: '6px',
            width: '100%',
          }}
          value={validateForm.bankName}
          onChange={(e) => setValidateForm({ ...validateForm, bankName: e.target.value })}
        />
      </ContainerField>
      <ContainerField nameField="familyCardNumber">
        <InputBase
          sx={{
            backgroundColor: 'rgb(241, 243, 245)',
            input: { padding: '12px' },
            borderRadius: '6px',
            width: '100%',
          }}
          value={validateForm.familyCardNumber}
          onChange={(e) => setValidateForm({ ...validateForm, familyCardNumber: e.target.value })}
        />
      </ContainerField>
      <ContainerField nameField="familyCardNumber">
        <InputBase
          sx={{
            backgroundColor: 'rgb(241, 243, 245)',
            input: { padding: '12px' },
            borderRadius: '6px',
            width: '100%',
          }}
          value={validateForm.familyCardNumber}
          onChange={(e) => setValidateForm({ ...validateForm, familyCardNumber: e.target.value })}
        />
      </ContainerField>
      <ContainerField nameField="siNo">
        <InputBase
          sx={{
            backgroundColor: 'rgb(241, 243, 245)',
            input: { padding: '12px' },
            borderRadius: '6px',
            width: '100%',
          }}
          value={validateForm.safetyInsuranceNo}
          onChange={(e) => setValidateForm({ ...validateForm, safetyInsuranceNo: e.target.value })}
        />
      </ContainerField>
      <ContainerField nameField="hiNo">
        <InputBase
          sx={{
            backgroundColor: 'rgb(241, 243, 245)',
            input: { padding: '12px' },
            borderRadius: '6px',
            width: '100%',
          }}
          value={validateForm.healthInsuranceNo}
          onChange={(e) => setValidateForm({ ...validateForm, healthInsuranceNo: e.target.value })}
        />
      </ContainerField>

      {fields2.map((item: any, index: number) => (
        <Grid
          container
          spacing={2}
          key={index}
          sx={{
            '&:first-of-type': { marginLeft: '0', marginTop: 0 },
            marginTop: '0 !important',
            marginLeft: '-16px !important',
          }}
        >
          <Grid
            item
            xs={12}
            md={5}
            xl={4}
            sx={{
              paddingLeft: '0 !important',
              paddingTop: '0 !important',
              display: 'flex',
              alignItems: 'center',
              marginTop: '0 !important',
            }}
          >
            <Typography
              variant="body2"
              gutterBottom
              sx={{
                fontFamily: 'SVN-Sofia-Regular',
                display: 'flex',
                alignItems: 'center',
                margin: 0,
                fontSize: '16px',
              }}
            >
              <FormattedMessage id={item.field} />
              <Box component="span" sx={{ color: 'rgb(229, 72, 77)', margin: 0 }}>
                {item.required}
              </Box>
            </Typography>
          </Grid>
          <Grid item xs={12} md={7} xl={8} sx={{ paddingLeft: '0 !important', paddingTop: '0 !important' }}>
            {item.input === 'input' && (
              <InputBase
                sx={{
                  backgroundColor: 'rgb(241, 243, 245)',
                  input: { padding: '12px' },
                  borderRadius: '6px',
                  width: '100%',
                }}
                type={item.type ? 'text' : 'number'}
              />
            )}
            {item.input === 'selected' && (
              <Selected
                defaultOption="Choose Marriage Status"
                objectOption={['N/A', 'Married with 1 kid', 'Single', 'Married']}
              />
            )}
          </Grid>
        </Grid>
      ))}
    </Stack>
  );
}

export default InfoForm2;
